","keccak256":"0xb229e8c09ab0a6b5f517c3537adfe08d26a46fc3ea46031ace67a00fb1900840","license":"GPL-2.0"},"contracts/core/YieldManager.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../interfaces/IBiosRewards.sol";
import "../interfaces/IIntegrationMap.sol";
import "../interfaces/IIntegration.sol";
import "../interfaces/IAMMIntegration.sol";
import "../interfaces/IEtherRewards.sol";
import "../interfaces/IYieldManager.sol";
import "../interfaces/ISushiSwapTrader.sol";
import "../interfaces/IUserPositions.sol";
import "../interfaces/IWeth9.sol";
import "../interfaces/IStrategyMap.sol";
import "../interfaces/ISwapManager.sol";
import "../interfaces/IUnwrapper.sol";
import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

/// @title Yield Manager
/// @notice Manages yield deployments, harvesting, processing, and distribution
contract YieldManager is Initializable, ModuleMapConsumer, Controlled, IYieldManager {
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    uint256 private gasAccountTargetEthBalance;
    uint32 private biosBuyBackEthWeight;
    uint32 private treasuryEthWeight;
    uint32 private protocolFeeEthWeight;
    uint32 private rewardsEthWeight;
    uint256 private lastEthRewardsAmount;

    address payable private gasAccount;
    address payable private treasuryAccount;

    mapping(address => uint256) private processedWethByToken;
    mapping(address => uint256) private lastHarvestTimestampByIntegration;

    // strategyId => StrategyInfo
    mapping(uint256 => StrategyInfo) private strategies;

    event HarvestYield(uint256 strategyId, address[] integration, uint256 amount);

    receive() external payable {}

    /// @param controllers_ The addresses of the controlling contracts
    /// @param moduleMap_ Address of the Module Map
    /// @param gasAccountTargetEthBalance_ The target ETH balance of the gas account
    /// @param biosBuyBackEthWeight_ The relative weight of ETH to send to BIOS buy back
    /// @param treasuryEthWeight_ The relative weight of ETH to send to the treasury
    /// @param protocolFeeEthWeight_ The relative weight of ETH to send to protocol fee accrual
    /// @param rewardsEthWeight_ The relative weight of ETH to send to user rewards
    /// @param gasAccount_ The address of the account to send ETH to gas for executing bulk system functions
    /// @param treasuryAccount_ The address of the system treasury account
    function initialize(
        address[] memory controllers_,
        address moduleMap_,
        uint256 gasAccountTargetEthBalance_,
        uint32 biosBuyBackEthWeight_,
        uint32 treasuryEthWeight_,
        uint32 protocolFeeEthWeight_,
        uint32 rewardsEthWeight_,
        address payable gasAccount_,
        address payable treasuryAccount_
    ) public initializer {
        require(gasAccount_ != address(0), "bad address");
        require(treasuryAccount_ != address(0), "bad address");

        __Controlled_init(controllers_, moduleMap_);
        gasAccountTargetEthBalance = gasAccountTargetEthBalance_;
        biosBuyBackEthWeight = biosBuyBackEthWeight_;
        treasuryEthWeight = treasuryEthWeight_;
        protocolFeeEthWeight = protocolFeeEthWeight_;
        rewardsEthWeight = rewardsEthWeight_;
        gasAccount = gasAccount_;
        treasuryAccount = treasuryAccount_;
    }

    /// @param gasAccountTargetEthBalance_ The target ETH balance of the gas account
    function updateGasAccountTargetEthBalance(uint256 gasAccountTargetEthBalance_) external override onlyController {
        gasAccountTargetEthBalance = gasAccountTargetEthBalance_;
    }

    /// @param biosBuyBackEthWeight_ The relative weight of ETH to send to BIOS buy back
    /// @param treasuryEthWeight_ The relative weight of ETH to send to the treasury
    /// @param protocolFeeEthWeight_ The relative weight of ETH to send to protocol fee accrual
    /// @param rewardsEthWeight_ The relative weight of ETH to send to user rewards
    function updateEthDistributionWeights(
        uint32 biosBuyBackEthWeight_,
        uint32 treasuryEthWeight_,
        uint32 protocolFeeEthWeight_,
        uint32 rewardsEthWeight_
    ) external override onlyController {
        biosBuyBackEthWeight = biosBuyBackEthWeight_;
        treasuryEthWeight = treasuryEthWeight_;
        protocolFeeEthWeight = protocolFeeEthWeight_;
        rewardsEthWeight = rewardsEthWeight_;
    }

    /// @param gasAccount_ The address of the account to send ETH to gas for executing bulk system functions
    function updateGasAccount(address payable gasAccount_) external override onlyController {
        require(gasAccount_ != address(0), "bad address");
        gasAccount = gasAccount_;
    }

    /// @param treasuryAccount_ The address of the system treasury account
    function updateTreasuryAccount(address payable treasuryAccount_) external override onlyController {
        require(treasuryAccount_ != address(0), "bad address");
        treasuryAccount = treasuryAccount_;
    }

    /// @notice Deploys all tokens to all integrations according to configured weights
    function deploy(DeployRequest[] calldata deployments) external override onlyController {
        IStrategyMap strategyMap = IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap));

        for (uint256 i = 0; i < deployments.length; i++) {
            if (deployments[i].ammPoolID > 0) {
                require(deployments[i].tokens.length <= 2, "too many tokens");
            }

            for (uint256 j = 0; j < deployments[i].tokens.length; j++) {
                int256 deployAmount = strategyMap.getDeployAmount(
                    deployments[i].integration,
                    deployments[i].ammPoolID,
                    deployments[i].tokens[j]
                );

                uint256 reserveBalance = IERC20MetadataUpgradeable(deployments[i].tokens[j]).balanceOf(
                    moduleMap.getModuleAddress(Modules.Kernel)
                );
                if (deployAmount > 0 && reserveBalance < abs(deployAmount)) {
                    strategyMap.closePositionsForWithdrawal(deployments[i].tokens[j], type(uint256).max);
                    deployAmount = strategyMap.getDeployAmount(
                        deployments[i].integration,
                        deployments[i].ammPoolID,
                        deployments[i].tokens[j]
                    );
                }

                if (deployments[i].ammPoolID > 0) {
                    IAMMIntegration integration = IAMMIntegration(deployments[i].integration);

                    if (deployAmount > 0) {
                        uint256 balanceBefore = IERC20MetadataUpgradeable(deployments[i].tokens[j]).balanceOf(
                            deployments[i].integration
                        );

                        IERC20MetadataUpgradeable(deployments[i].tokens[j]).safeTransferFrom(
                            moduleMap.getModuleAddress(Modules.Kernel),
                            deployments[i].integration,
                            abs(deployAmount)
                        );
                        uint256 balanceAfter = IERC20MetadataUpgradeable(deployments[i].tokens[j]).balanceOf(
                            deployments[i].integration
                        );
                        integration.deposit(
                            deployments[i].tokens[j],
                            balanceAfter - balanceBefore,
                            deployments[i].ammPoolID
                        );
                        integration.deploy(deployments[i].ammPoolID);
                    } else if (deployAmount < 0) {
                        integration.withdraw(deployments[i].tokens[j], abs(deployAmount), deployments[i].ammPoolID);
                    }
                } else {
                    IIntegration integration = IIntegration(deployments[i].integration);
                    if (deployAmount > 0) {
                        uint256 balanceBefore = IERC20MetadataUpgradeable(deployments[i].tokens[j]).balanceOf(
                            deployments[i].integration
                        );
                        IERC20MetadataUpgradeable(deployments[i].tokens[j]).safeTransferFrom(
                            moduleMap.getModuleAddress(Modules.Kernel),
                            deployments[i].integration,
                            abs(deployAmount)
                        );
                        uint256 balanceAfter = IERC20MetadataUpgradeable(deployments[i].tokens[j]).balanceOf(
                            deployments[i].integration
                        );

                        integration.deposit(deployments[i].tokens[j], balanceAfter - balanceBefore);
                        integration.deploy();
                    } else if (deployAmount < 0) {
                        integration.withdraw(deployments[i].tokens[j], abs(deployAmount));
                    }
                }
                strategyMap.decreaseDeployAmountChange(
                    deployments[i].integration,
                    deployments[i].ammPoolID,
                    deployments[i].tokens[j],
                    abs(deployAmount)
                );
            }
            strategyMap.clearClosablePositions(deployments[i].tokens);
        }
    }

    function abs(int256 val) internal pure returns (uint256) {
        return uint256(val >= 0 ? val : -val);
    }

    function _calculateReserveAmount(
        uint256 amount,
        uint256 numerator,
        uint256 denominator
    ) internal pure returns (uint256) {
        return (amount == 0 ? 1 : amount * numerator) / denominator;
    }

    /// @notice Harvests available yield from provided and integration, credited to strategy
    /// @param strategyId Strategy to credit the yield to
    /// @param integrations The integrations to harvest yield from
    function harvestYield(
        uint256 strategyId,
        address[] calldata integrations,
        uint256 amount,
        bool setAccounting
    ) external override onlyManager {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        address wethAddress = integrationMap.getWethTokenAddress();
        IERC20MetadataUpgradeable weth = IERC20MetadataUpgradeable(wethAddress);

        // Harvest yield and account the weth
        uint256 balanceBefore = weth.balanceOf(address(this));
        for (uint256 i; i < integrations.length; ++i) {
            IIntegration(integrations[i]).harvestYield();
        }

        uint256 receivedWeth = weth.balanceOf(address(this)) - balanceBefore;

        // update accounting
        if (setAccounting) {
            StrategyInfo storage strategy = strategies[strategyId];

            uint256 updateAmount = amount > 0 ? amount : receivedWeth;

            strategy.lastHarvest = block.timestamp;
            strategy.processedWeth += updateAmount;
        }

        emit HarvestYield(strategyId, integrations, receivedWeth);
    }

    function strategyAccounting(
        uint256 strategyId,
        uint256 amount,
        bool add
    ) external override onlyManager {
        StrategyInfo storage strategy = strategies[strategyId];

        strategy.lastHarvest = block.timestamp;

        if (add) {
            strategy.processedWeth += amount;
        } else {
            strategy.processedWeth -= amount;
        }
    }

    /// @notice Distributes ETH to the gas account, BIOS buy back, treasury, protocol fee accrual, and user rewards
    /// @param ids An aray of strategy IDs to distribute eth for
    /// @param positions An array of the total position value for the strategy to prevent flashing
    /// @param biosAmount Amount of bios deposited to prevent flashing
    function distributeEth(
        uint256[] calldata ids,
        uint256[] calldata positions,
        uint256 biosAmount
    ) external override onlyController {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        address wethAddress = integrationMap.getWethTokenAddress();

        if (biosAmount > 0) {
            // Check against BIOS flashing
            require(
                IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions)).totalTokenBalance(
                    integrationMap.getBiosTokenAddress()
                ) <= biosAmount,
                "YieldManager::expected BIOS exceeded"
            );
        }

        // First fill up gas wallet with ETH
        // We just skip this for now :)
        // If you are going to enable, don't forget to account for where
        // the eth will come from!!
        // uint256 gasAmount = _ethToGasAccount();

        uint256 totalRewards;

        for (uint256 i; i < ids.length; ++i) {
            // Don't run this for BIOS strat :p
            require(ids[i] > 0, "YieldManager::invalid strategy");

            // check positions to prevent flasher
            require(
                IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).getStrategyTokenBalance(
                    ids[i],
                    wethAddress
                ) <= positions[i],
                "YieldManager::position exceeded"
            );

            StrategyInfo storage strategy = strategies[ids[i]];

            uint256 wethToDistribute = strategy.processedWeth - strategy.distributedWeth;

            if (wethToDistribute > 0) {
                (
                    uint256 biosBuyBackWethAmount,
                    uint256 treasuryWethAmount,
                    uint256 protocolFeeWethAmount,
                    uint256 rewardsWethAmount
                ) = _calculateRewardAmounts(wethToDistribute);

                // Send WETH to SushiSwap trader for BIOS buy back
                if (biosBuyBackWethAmount > 0) {
                    IERC20MetadataUpgradeable(wethAddress).safeTransfer(
                        moduleMap.getModuleAddress(Modules.SushiSwapTrader),
                        biosBuyBackWethAmount
                    );
                }

                // Swap WETH for ETH and transfer to the treasury account
                _unwrapWeth(treasuryWethAmount, wethAddress);
                _transferAmount(treasuryWethAmount, treasuryAccount);

                if (protocolFeeWethAmount > 0) {
                    // Send ETH to protocol fee accrual rewards (BIOS stakers)
                    _ethToProtocolFeeAccrual(protocolFeeWethAmount);
                }

                // Send ETH to strategy rewards
                _ethToRewards(ids[i], rewardsWethAmount);
                totalRewards += rewardsWethAmount;

                // Update accounting
                strategy.distributedWeth += wethToDistribute;
            }
        }

        // Update stats metric
        lastEthRewardsAmount = totalRewards;
    }

    /// @notice Distributes WETH to gas wallet
    function _ethToGasAccount() private returns (uint256 ethAmountToGasAccount) {
        address wethAddress = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress();
        uint256 wethBalance = IERC20MetadataUpgradeable(wethAddress).balanceOf(address(this));

        if (wethBalance > 0) {
            uint256 gasAccountActualEthBalance = gasAccount.balance;
            if (gasAccountActualEthBalance < gasAccountTargetEthBalance) {
                // Need to send ETH to gas account
                if (wethBalance < gasAccountTargetEthBalance - gasAccountActualEthBalance) {
                    // Send all of WETH to gas wallet
                    ethAmountToGasAccount = wethBalance;
                } else {
                    // Send portion of WETH to gas wallet
                    ethAmountToGasAccount = gasAccountTargetEthBalance - gasAccountActualEthBalance;
                }
                _unwrapWeth(ethAmountToGasAccount, wethAddress);
                _transferAmount(ethAmountToGasAccount, gasAccount);
            }
        }
    }

    /// @notice Uses any WETH held in the SushiSwap trader to buy back BIOS which is sent to the Kernel
    /// @param ratioX1000 BIOS/WETH ratio for front runner protection
    function biosBuyBack(uint256 ratioX1000) external override onlyController {
        if (
            IERC20MetadataUpgradeable(
                IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress()
            ).balanceOf(moduleMap.getModuleAddress(Modules.SushiSwapTrader)) > 0
        ) {
            // Use all ETH sent to the SushiSwap trader to buy BIOS
            ISushiSwapTrader(moduleMap.getModuleAddress(Modules.SushiSwapTrader)).biosBuyBack(ratioX1000);

            // Use all BIOS transferred to the Kernel to increase bios rewards
            IBiosRewards(moduleMap.getModuleAddress(Modules.BiosRewards)).increaseBiosRewards();
        }
    }

    /// @notice Distributes ETH to Rewards per token
    /// @param strategyId The Strategy ID to distribute the rewards to
    /// @param ethRewardsAmount The amount of ETH rewards to distribute
    function _ethToRewards(uint256 strategyId, uint256 ethRewardsAmount) private {
        if (ethRewardsAmount > 0) {
            // Distribute eth rewards
            IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).increaseEthRewards(
                strategyId,
                ethRewardsAmount
            );

            // Convert to eth and send
            _unwrapWeth(
                ethRewardsAmount,
                IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress()
            );
            _transferAmount(ethRewardsAmount, moduleMap.getModuleAddress(Modules.Kernel));
        }
    }

    /// @notice Distributes ETH to protocol fee accrual (BIOS staker rewards)
    /// @param protocolFeeEthRewardsAmount Amount of ETH to distribute to protocol fee accrual
    function _ethToProtocolFeeAccrual(uint256 protocolFeeEthRewardsAmount) private {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));

        if (
            IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions)).totalTokenBalance(
                integrationMap.getBiosTokenAddress()
            ) > 0
        ) {
            // BIOS has been deposited, increase Ether rewards for BIOS depositors
            IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).increaseEthRewards(
                0,
                protocolFeeEthRewardsAmount
            );

            _unwrapWeth(protocolFeeEthRewardsAmount, integrationMap.getWethTokenAddress());

            _transferAmount(protocolFeeEthRewardsAmount, moduleMap.getModuleAddress(Modules.Kernel));
        } else {
            // No BIOS has been deposited, send WETH back to Kernel as reserves
            IERC20MetadataUpgradeable(integrationMap.getWethTokenAddress()).transfer(
                moduleMap.getModuleAddress(Modules.Kernel),
                protocolFeeEthRewardsAmount
            );
        }
    }

    function _calculateRewardAmounts(uint256 amount)
        internal
        view
        returns (
            uint256 biosBuyBackAmount,
            uint256 treasuryAmount,
            uint256 protocolFeeAmount,
            uint256 rewardsAmount
        )
    {
        uint256 weightSum = getEthWeightSum();
        biosBuyBackAmount = (amount * biosBuyBackEthWeight) / weightSum;
        treasuryAmount = (amount * treasuryEthWeight) / weightSum;
        protocolFeeAmount = (amount * protocolFeeEthWeight) / weightSum;
        rewardsAmount = amount - biosBuyBackAmount - treasuryAmount - protocolFeeAmount;
    }

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The amount of the token held in the Kernel as reserves
    function getReserveTokenBalance(address tokenAddress) public view override returns (uint256) {
        require(
            IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getIsTokenAdded(tokenAddress),
            "YieldManager::getReserveTokenBalance: Token not added"
        );
        return IERC20MetadataUpgradeable(tokenAddress).balanceOf(moduleMap.getModuleAddress(Modules.Kernel));
    }

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The desired amount of the token to hold in the Kernel as reserves
    function getDesiredReserveTokenBalance(address tokenAddress) public view override returns (uint256) {
        require(
            IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getIsTokenAdded(tokenAddress),
            "YieldManager::getDesiredReserveTokenBalance: Token not added"
        );
        uint256 tokenReserveRatioNumerator = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap))
            .getTokenReserveRatioNumerator(tokenAddress);
        uint256 tokenTotalBalance = IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).getTokenTotalBalance(
            tokenAddress
        );
        return
            (tokenTotalBalance * tokenReserveRatioNumerator) /
            IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getReserveRatioDenominator();
    }

    /// @return ethWeightSum The sum of ETH distribution weights
    function getEthWeightSum() public view override returns (uint32 ethWeightSum) {
        ethWeightSum = biosBuyBackEthWeight + treasuryEthWeight + protocolFeeEthWeight + rewardsEthWeight;
    }

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return tokenTotalIntegrationBalance The total amount of the token that can be withdrawn from integrations
    function getTokenTotalIntegrationBalance(address tokenAddress)
        public
        view
        override
        returns (uint256 tokenTotalIntegrationBalance)
    {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        uint256 integrationCount = integrationMap.getIntegrationAddressesLength();

        for (uint256 integrationId; integrationId < integrationCount; integrationId++) {
            tokenTotalIntegrationBalance += IIntegration(integrationMap.getIntegrationAddress(integrationId))
                .getBalance(tokenAddress);
        }
    }

    /// @return The address of the gas account
    function getGasAccount() public view override returns (address) {
        return gasAccount;
    }

    /// @return The address of the treasury account
    function getTreasuryAccount() public view override returns (address) {
        return treasuryAccount;
    }

    /// @return The last amount of ETH distributed to rewards
    function getLastEthRewardsAmount() public view override returns (uint256) {
        return lastEthRewardsAmount;
    }

    /// @return The target ETH balance of the gas account
    function getGasAccountTargetEthBalance() public view override returns (uint256) {
        return gasAccountTargetEthBalance;
    }

    /// @return The BIOS buyback ETH weight
    /// @return The Treasury ETH weight
    /// @return The Protocol fee ETH weight
    /// @return The rewards ETH weight
    function getEthDistributionWeights()
        public
        view
        override
        returns (
            uint32,
            uint32,
            uint32,
            uint32
        )
    {
        return (biosBuyBackEthWeight, treasuryEthWeight, protocolFeeEthWeight, rewardsEthWeight);
    }

    /// @return The timestamp the harvestYield function was last called
    function getLastHarvestYieldTimestamp(address integrationAddress) external view returns (uint256) {
        return lastHarvestTimestampByIntegration[integrationAddress];
    }

    function getStrategyInfo(uint256 strategyId) external view returns (StrategyInfo memory) {
        return strategies[strategyId];
    }

    function transferClosedPositionsValue(
        address destination,
        address token,
        uint256 amount
    ) external override onlyController {
        require(destination != address(0), "bad address");
        IERC20MetadataUpgradeable(token).safeTransfer(destination, amount);
    }

    function getAllPendingYield(address token) external view returns (IntegrationYield[] memory pendingYieldData) {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        uint256 integrationCount = integrationMap.getIntegrationAddressesLength();

        pendingYieldData = new IntegrationYield[](integrationCount);

        for (uint256 integrationId; integrationId < integrationCount; integrationId++) {
            address integrationAddress = integrationMap.getIntegrationAddress(integrationId);
            uint256 pendingYield = IIntegration(integrationAddress).getPendingYield(token);

            pendingYieldData[integrationId] = IntegrationYield(integrationAddress, token, pendingYield);
        }
    }

    function _transferAmount(uint256 amount, address to) internal {
        (bool sent, ) = payable(to).call{value: amount}("");
        require(sent, "Failed to transfer value");
    }

    function _unwrapWeth(uint256 amount, address wethAddress) internal {
        try moduleMap.getModuleAddress(Modules.Unwrapper) returns (address unwrapper) {
            if (unwrapper != address(0)) {
                // the unwrapper is set, so use it
                IERC20MetadataUpgradeable(wethAddress).safeTransfer(unwrapper, amount);
                IUnwrapper(payable(unwrapper)).unwrap(amount);
            } else {
                // No unwrapper, so just do it
                IWeth9(wethAddress).withdraw(amount);
            }
        } catch {
            // No module, so just do it
            IWeth9(wethAddress).withdraw(amount);
        }
    }
}
