import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "../interfaces/ISwapManager.sol";
import "../interfaces/IDexTrader.sol";

import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

contract SwapManager is Initializable, ModuleMapConsumer, Controlled, ISwapManager {
    using EnumerableSet for EnumerableSet.AddressSet;
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    // Addresses of dex traders.
    // For easy adding new dexes, this contract use traders addresses
    // instead of enum values in moduleMap.
    EnumerableSet.AddressSet traders;
    // address of the contract (user) which will be used for internal swaps
    address internalWallet;

    function initialize(address[] memory _controllers, address _moduleMap) external initializer {
        __Controlled_init(_controllers, _moduleMap);
        internalWallet = moduleMap.getModuleAddress(Modules.Kernel);
    }

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _recipient The address of the token out recipient
    /// @param _amountIn The exact amount of the input to swap
    /// @return tradeSuccess_ Indicates whether the swap succeeded
    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn
    ) external override onlyController returns (bool tradeSuccess_) {
        swapExactIn(_tokenIn, _tokenOut, _recipient, _amountIn, 0);
    }

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _recipient The address of the token out recipient
    /// @param _amountIn The exact amount of the input to swap
    /// @param _minAmountOut The minimum amount of _tokenOut to receive
    /// @return tradeSuccess_ Indicates whether the swap succeeded
    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _minAmountOut
    ) public override onlyController returns (bool tradeSuccess_) {
        (address bestTrader, uint256 minOut) = getBestTraderForTheSwap(_tokenIn, _tokenOut, _amountIn);

        uint256 trueMinAmountOut = minOut >= _minAmountOut ? minOut : _minAmountOut;

        tradeSuccess_ = swapUsingInternalWallet(_tokenIn, _tokenOut, _recipient, _amountIn, trueMinAmountOut);
        if (tradeSuccess_) return tradeSuccess_;

        IDexTrader trader = IDexTrader(bestTrader);
        IERC20MetadataUpgradeable(_tokenIn).safeTransfer(bestTrader, _amountIn);
        tradeSuccess_ = trader.swapExactInput(_tokenIn, _tokenOut, _recipient, _amountIn, trueMinAmountOut);
    }

    /// @param _traderAddress Address of the contract implementing IDexTrader interface
    function addTrader(address _traderAddress) external onlyManager {
        require(!traders.contains(_traderAddress), "SwapManager::addTrader: Trader already added");
        traders.add(_traderAddress);
    }

    /// @param _traderAddress Address of the contract implementing IDexTrader interface
    function removeTrader(address _traderAddress) external onlyManager {
        require(traders.contains(_traderAddress), "SwapManager::removeTrader: Trader was not added");
        traders.remove(_traderAddress);
    }

    /// @param _walletAddress The address of the contract (user) which will be used for internal swaps
    function setInternalWallet(address _walletAddress) external onlyManager {
        internalWallet = _walletAddress;
    }

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _amountIn The exact amount of the input to swap
    /// @return bestTrader_ Address of found trader
    /// @return minAmountOut_ The minimum amount of tokenOut to receive, factoring in allowable slippage
    /// @dev iterates over all added traders and finds best for swapping
    /// @dev uses getAmountOut() for comparing
    function getBestTraderForTheSwap(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) internal returns (address bestTrader_, uint256 minAmountOut_) {
        uint256 maxAmountOut;
        for (uint8 i = 0; i < traders.length(); i++) {
            IDexTrader trader = IDexTrader(traders.at(i));
            uint256 amountOut;
            uint256 amountOutMin;

            if (trader.isPoolExist(_tokenIn, _tokenOut)) {
                amountOut = trader.getAmountOut(_tokenIn, _tokenOut, _amountIn);
                amountOutMin = trader.getAmountOutMinimum(_tokenIn, _tokenOut, _amountIn);
            }

            if (amountOut > maxAmountOut) {
                bestTrader_ = traders.at(i);
                minAmountOut_ = amountOutMin;
                maxAmountOut = amountOut;
            }
        }
    }

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _recipient The address of the token out recipient
    /// @param _amountIn The exact amount of the input to swap
    /// @param _amountOutMin The minimum amount of tokenOut to receive from the swap
    /// @return tradeSuccess_ Indicates whether the swap succeeded
    /// @dev tries to swap tokens using internal wallet instead of external dexes
    function swapUsingInternalWallet(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) internal returns (bool tradeSuccess_) {
        IERC20MetadataUpgradeable tokenIn = IERC20MetadataUpgradeable(_tokenIn);
        IERC20MetadataUpgradeable tokenOut = IERC20MetadataUpgradeable(_tokenOut);

        uint256 walletTokenOutBalance = tokenOut.balanceOf(internalWallet);
        uint256 walletAllowanceToThis = tokenOut.allowance(internalWallet, address(this));

        if (walletTokenOutBalance >= _amountOutMin && walletAllowanceToThis >= _amountOutMin && _amountOutMin > 0) {
            tokenOut.safeTransferFrom(internalWallet, address(this), _amountOutMin);
            tokenOut.safeTransfer(_recipient, _amountOutMin);
            tokenIn.safeTransfer(internalWallet, _amountIn);
            tradeSuccess_ = true;
        }
    }
}
