interface IDexTrader {
    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _recipient The address of the token out recipient
    /// @param _amountIn The exact amount of the input to swap
    /// @param _amountOutMin The minimum amount of tokenOut to receive from the swap
    /// @return bool Indicates whether the swap succeeded
    function swapExactInput(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) external returns (bool);

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _amountIn The exact amount of the input to swap
    /// @return uint256 The estimated amount of tokenOut to receive
    function getAmountOut(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external returns (uint256);

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _amountIn The exact amount of the input to swap
    /// @return uint256 The minimum amount of tokenOut to receive, factoring in allowable slippage
    function getAmountOutMinimum(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external returns (uint256);

    /// @param _tokenA The address of the first  token from the token pair
    /// @param _tokenB The address of the second token from the token pair
    /// return true if pool exist else overwise
    function isPoolExist(address _tokenA, address _tokenB) external view returns (bool);
}
