","keccak256":"0xb229e8c09ab0a6b5f517c3537adfe08d26a46fc3ea46031ace67a00fb1900840","license":"GPL-2.0"},"contracts/core/UserPositions.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../interfaces/IIntegrationMap.sol";
import "../interfaces/IBiosRewards.sol";
import "../interfaces/IEtherRewards.sol";
import "../interfaces/IUserPositions.sol";
import "../interfaces/IWeth9.sol";
import "../interfaces/IYieldManager.sol";
import "../interfaces/IIntegration.sol";
import "../interfaces/IAMMIntegration.sol";
import "../interfaces/IStrategyMap.sol";
import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

/// @title User Positions
/// @notice Allows users to deposit/withdraw erc20 tokens
contract UserPositions is Initializable, ModuleMapConsumer, Controlled, IUserPositions {
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    /// @dev This is deprecated - now live in BiosRewards.sol
    uint32 private _biosRewardsDuration;

    // Token address => total supply held by the contract
    mapping(address => uint256) private _totalSupply;

    // Token address => User address => Balance of tokens a user has deposited
    mapping(address => mapping(address => uint256)) private _balances;

    // User => Token => deployed balance
    mapping(address => mapping(address => uint256)) private _deployedBalances;

    // User => strategy => token => balance
    mapping(address => mapping(uint256 => mapping(address => uint256))) private _userStrategyBalances;

    // Token address => User address => Balance of tokens a user has from interconnecting from other chains
    mapping(address => mapping(address => uint256)) private _interconnectBalances;

    /// @param controllers_ The addresses of the controlling contracts
    /// @param moduleMap_ Address of the Module Map
    function initialize(address[] memory controllers_, address moduleMap_) public initializer {
        __Controlled_init(controllers_, moduleMap_);
    }

    /// @notice User is allowed to deposit whitelisted tokens
    /// @param depositor Address of the account depositing
    /// @param tokens Array of token the token addresses
    /// @param amounts Array of token amounts
    /// @param ethAmount The amount of ETH sent with the deposit
    function deposit(
        address depositor,
        address[] memory tokens,
        uint256[] memory amounts,
        uint256 ethAmount,
        bool migration
    ) external override onlyController {
        require(depositor != address(0), "bad address");
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        address biosAddress = integrationMap.getBiosTokenAddress();

        uint256[] memory actualAmounts = new uint256[](tokens.length);

        for (uint256 tokenId; tokenId < tokens.length; tokenId++) {
            // Token must be accepting deposits
            require(
                integrationMap.getTokenAcceptingDeposits(tokens[tokenId]),
                "UserPositions::deposit: This token is not accepting deposits"
            );

            require(amounts[tokenId] > 0, "UserPositions::deposit: Deposit amount must be greater than zero");
            uint256 actualAmount;
            if (migration) {
                actualAmount = amounts[tokenId];
            } else {
                IERC20MetadataUpgradeable erc20 = IERC20MetadataUpgradeable(tokens[tokenId]);
                // Get the balance before the transfer
                uint256 beforeBalance = erc20.balanceOf(moduleMap.getModuleAddress(Modules.Kernel));

                // Transfer the tokens from the depositor to the Kernel
                erc20.safeTransferFrom(depositor, moduleMap.getModuleAddress(Modules.Kernel), amounts[tokenId]);

                // Get the balance after the transfer
                uint256 afterBalance = erc20.balanceOf(moduleMap.getModuleAddress(Modules.Kernel));
                actualAmount = afterBalance - beforeBalance;
                // Increase rewards

                IBiosRewards(moduleMap.getModuleAddress(Modules.BiosRewards)).increaseRewards(
                    tokens[tokenId],
                    depositor,
                    actualAmount
                );
            }
            actualAmounts[tokenId] = actualAmount;

            // Update balances
            _totalSupply[tokens[tokenId]] += actualAmount;
            _balances[tokens[tokenId]][depositor] += actualAmount;
        }

        if (ethAmount > 0) {
            address wethAddress = integrationMap.getWethTokenAddress();

            // Increase rewards
            IBiosRewards(moduleMap.getModuleAddress(Modules.BiosRewards)).increaseRewards(
                wethAddress,
                depositor,
                ethAmount
            );

            // Update WETH balances
            _totalSupply[wethAddress] += ethAmount;
            _balances[wethAddress][depositor] += ethAmount;
        }

        emit Deposit(depositor, tokens, actualAmounts, ethAmount);
    }

    /// @notice User is allowed to withdraw tokens
    /// @param recipient The address of the user withdrawing
    /// @param tokens Array of token the token addresses
    /// @param amounts Array of token amounts
    /// @param withdrawWethAsEth Boolean indicating whether should receive WETH balance as ETH
    function withdraw(
        address recipient,
        address[] memory tokens,
        uint256[] memory amounts,
        bool withdrawWethAsEth
    ) external override onlyController returns (uint256 ethWithdrawn) {
        require(recipient != address(0), "bad address");
        ethWithdrawn = _withdraw(recipient, tokens, amounts, withdrawWethAsEth);
    }

    /// @notice Allows a user to withdraw entire balances of the specified tokens and claim rewards
    /// @param recipient The address of the user withdrawing tokens
    /// @param tokens Array of token address that user is exiting positions from
    /// @param strategies Array of strategy IDs that user is claiming rewards from
    /// @param withdrawWethAsEth Boolean indicating whether should receive WETH balance as ETH
    /// @return tokenAmounts The amounts of each token being withdrawn
    /// @return ethWithdrawn The amount of ETH being withdrawn
    /// @return ethClaimed The amount of ETH being claimed from rewards
    /// @return biosClaimed The amount of BIOS being claimed from rewards
    function withdrawAllAndClaim(
        address recipient,
        address[] calldata tokens,
        uint256[] calldata strategies,
        bool withdrawWethAsEth
    )
        external
        override
        onlyController
        returns (
            uint256[] memory tokenAmounts,
            uint256 ethWithdrawn,
            uint256 ethClaimed,
            uint256 biosClaimed
        )
    {
        require(recipient != address(0), "bad address");

        tokenAmounts = new uint256[](tokens.length);
        IBiosRewards biosRewards = IBiosRewards(moduleMap.getModuleAddress(Modules.BiosRewards));
        for (uint256 tokenId; tokenId < tokens.length; tokenId++) {
            tokenAmounts[tokenId] = userTokenBalance(tokens[tokenId], recipient);
        }

        ethWithdrawn = _withdraw(recipient, tokens, tokenAmounts, withdrawWethAsEth);

        if (IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).getUserEthRewards(recipient) > 0) {
            ethClaimed = _claimEthRewards(recipient, strategies);
        }

        biosClaimed = biosRewards.claimBiosRewards(recipient);
    }

    /// @notice User is allowed to withdraw tokens
    /// @param recipient The address of the user withdrawing
    /// @param tokens Array of token the token addresses
    /// @param amounts Array of token amounts
    /// @param withdrawWethAsEth Boolean indicating whether should receive WETH balance as ETH
    function _withdraw(
        address recipient,
        address[] memory tokens,
        uint256[] memory amounts,
        bool withdrawWethAsEth
    ) private returns (uint256 ethWithdrawn) {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        address wethAddress = integrationMap.getWethTokenAddress();
        address biosAddress = integrationMap.getBiosTokenAddress();
        ethWithdrawn = 0;
        require(
            tokens.length == amounts.length,
            "UserPositions::_withdraw: Tokens array length does not match amounts array length"
        );

        for (uint256 tokenId; tokenId < tokens.length; tokenId++) {
            if (amounts[tokenId] == 0) break;
            require(
                integrationMap.getTokenAcceptingWithdrawals(tokens[tokenId]),
                "UserPositions::_withdraw: This token is not accepting withdrawals"
            );
            require(
                amounts[tokenId] <= userTokenBalance(tokens[tokenId], recipient),
                "UserPositions::_withdraw: Withdraw amount exceeds user balance"
            );

            // Process user withdrawal amount management, and close out positions as needed to fund the withdrawal
            uint256 reserveBalance = IERC20MetadataUpgradeable(tokens[tokenId]).balanceOf(
                moduleMap.getModuleAddress(Modules.Kernel)
            );

            if (reserveBalance < amounts[tokenId]) {
                IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).closePositionsForWithdrawal(
                    tokens[tokenId],
                    amounts[tokenId]
                );
            }

            uint256 actualTransferAmount;
            if (tokens[tokenId] == wethAddress && withdrawWethAsEth) {
                ethWithdrawn = amounts[tokenId];
                actualTransferAmount = amounts[tokenId];
            } else {
                uint256 currentReserves = IERC20MetadataUpgradeable(tokens[tokenId]).balanceOf(
                    moduleMap.getModuleAddress(Modules.Kernel)
                );
                if (currentReserves < amounts[tokenId]) {
                    actualTransferAmount = currentReserves;
                } else {
                    actualTransferAmount = amounts[tokenId];
                }
                // Amounts recovered from the integrations for the user was lower than requested, likely due to fees (see yearn).
                IERC20MetadataUpgradeable(tokens[tokenId]).safeTransferFrom(
                    moduleMap.getModuleAddress(Modules.Kernel),
                    recipient,
                    actualTransferAmount
                );
            }

            // Decrease rewards
            IBiosRewards(moduleMap.getModuleAddress(Modules.BiosRewards)).decreaseRewards(
                tokens[tokenId],
                recipient,
                actualTransferAmount
            );

            // BIOS gets eth rewards decreased on withdraw, not strategy
            if (tokens[tokenId] == biosAddress) {
                IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).decreaseUserRewards(
                    0,
                    actualTransferAmount,
                    recipient
                );
            }

            // Update balances
            if (actualTransferAmount > _totalSupply[tokens[tokenId]]) {
                _totalSupply[tokens[tokenId]] = 0;
            } else {
                _totalSupply[tokens[tokenId]] -= actualTransferAmount;
            }
            _balances[tokens[tokenId]][recipient] -= actualTransferAmount;
        }
    }

    function abs(int256 val) internal pure returns (uint256) {
        return uint256(val >= 0 ? val : -val);
    }

    /// @param recipient The address of the user claiming ETH rewards
    /// @param strategies An array of strategyIDs to claim rewards for
    function claimEthRewards(address recipient, uint256[] calldata strategies)
        external
        override
        onlyController
        returns (uint256 ethClaimed)
    {
        require(recipient != address(0), "bad address");

        ethClaimed = _claimEthRewards(recipient, strategies);
    }

    /// @param recipient The address of the user claiming BIOS rewards
    function _claimEthRewards(address recipient, uint256[] calldata strategies) private returns (uint256 ethClaimed) {
        ethClaimed = IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).claimEthRewards(
            recipient,
            strategies
        );
    }

    /// @param asset Address of the ERC20 token contract
    /// @return The total balance of the asset deposited in the system
    function totalTokenBalance(address asset) public view override returns (uint256) {
        return _totalSupply[asset];
    }

    /// @param asset Address of the ERC20 token contract
    /// @param account Address of the user account
    function userTokenBalance(address asset, address account) public view override returns (uint256) {
        if (_deployedBalances[account][asset] >= _balances[asset][account]) {
            return 0;
        }
        return _balances[asset][account] - _deployedBalances[account][asset];
    }

    /// @param asset Address of the ERC20 token contract
    /// @param account Address of the user account
    function userInterconnectBalance(address asset, address account) public view override returns (uint256) {
        return _interconnectBalances[asset][account];
    }

    /// @param asset Address of the ERC20 token contract
    /// @param account Address of the user account
    function userDeployableBalance(address asset, address account) public view override returns (uint256) {
        uint256 localAndForeignBalance = _balances[asset][account] + _interconnectBalances[asset][account];
        if (_deployedBalances[account][asset] >= localAndForeignBalance) {
            return 0;
        }
        return localAndForeignBalance - _deployedBalances[account][asset];
    }

    function enterStrategy(uint256 strategyID, TokenMovement[] calldata tokens) external override {
        _enterStrategy(strategyID, msg.sender, tokens, false);
        IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).increaseStrategy(strategyID, tokens);
    }

    function _remoteStrategyExecution(
        uint256 strategyID,
        address user,
        TokenMovement[] calldata tokens,
        bool enterStrat
    ) external override onlyController {
        if (enterStrat) {
            _enterStrategy(strategyID, user, tokens, false);
            IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).increaseStrategy(strategyID, tokens);
        } else {
            _exitStrategy(strategyID, user, tokens);
        }
    }

    function exitStrategy(uint256 strategyID, TokenMovement[] calldata tokens) external override {
        _exitStrategy(strategyID, msg.sender, tokens);
    }

    function _exitStrategy(
        uint256 strategyID,
        address user,
        TokenMovement[] calldata tokens
    ) internal {
        require(tokens.length > 0, "tokens required");
        for (uint256 i; i < tokens.length; ++i) {
            require(tokens[i].token != address(0), "invalid token");
            require(tokens[i].amount > 0, "invalid amount");
            require(_userStrategyBalances[user][strategyID][tokens[i].token] >= tokens[i].amount, "insufficient funds");
            _deployedBalances[user][tokens[i].token] -= tokens[i].amount;
            _userStrategyBalances[user][strategyID][tokens[i].token] -= tokens[i].amount;
        }
        // update eth rewards
        IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).decreaseUserRewards(
            strategyID,
            tokens[0].amount,
            msg.sender
        );

        emit ExitStrategy(strategyID, user, tokens);

        IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).decreaseStrategy(strategyID, tokens);
    }

    function updateUserTokenBalances(
        address[] calldata assets,
        address account,
        uint256[] calldata amounts,
        bool[] memory add
    ) external override onlyController {
        require(account != address(0), "bad address");

        require(assets.length == amounts.length, "UserPositions::updateUserTokenBalances: arrays must be equal length");
        for (uint256 i = 0; i < assets.length; i++) {
            uint256 currBal = _balances[assets[i]][account];
            require(
                0 != amounts[i],
                "UserPositions::updateUserTokenBalances: Must have a positive or negative number to change the balance"
            );
            uint256 newAvailableAmount;
            if (add[i]) {
                newAvailableAmount = currBal + amounts[i];
            } else {
                require(
                    amounts[i] <= currBal,
                    "UserPositions::updateUserTokenBalances: Amount to reduce balance must be no more than current balance"
                );
                newAvailableAmount = currBal - amounts[i];
            }
            _balances[assets[i]][account] = newAvailableAmount;
        }
    }

    function updateUserInterconnectBalances(
        address[] memory assets,
        address account,
        uint256[] memory amounts,
        bool[] memory add
    ) external override onlyController {
        require(account != address(0), "bad address");

        require(
            assets.length == amounts.length,
            "UserPositions::updateUserInterconnectBalances: arrays must be equal length"
        );
        for (uint256 i = 0; i < assets.length; i++) {
            uint256 currBal = _interconnectBalances[assets[i]][account];
            require(
                0 != amounts[i],
                "UserPositions::updateUserInterconnectBalances: Must have a positive or negative number to change the balance"
            );
            uint256 newAvailableAmount;
            if (add[i]) {
                newAvailableAmount = currBal + amounts[i];
            } else {
                require(
                    amounts[i] <= currBal,
                    "UserPositions::updateUserInterconnectBalances: Amount to reduce balance must be no more than current balance"
                );
                newAvailableAmount = currBal - amounts[i];
            }
            _interconnectBalances[assets[i]][account] = newAvailableAmount;
        }
    }

    function getUserStrategyBalanceByToken(
        uint256 id,
        address token,
        address user
    ) external view override returns (uint256 amount) {
        return _userStrategyBalances[user][id][token];
    }

    function getUserInvestedAmountByToken(address token, address user) external view override returns (uint256 amount) {
        return _deployedBalances[user][token];
    }

    function getUserBalances(
        address user,
        uint256[] calldata _strategies,
        address[] calldata _tokens
    )
        external
        view
        override
        returns (
            IStrategyMap.StrategyBalance[] memory strategyBalance,
            IStrategyMap.GeneralBalance[] memory userBalance
        )
    {
        strategyBalance = new IStrategyMap.StrategyBalance[](_strategies.length);
        userBalance = new IStrategyMap.GeneralBalance[](_tokens.length);

        for (uint256 i = 0; i < _tokens.length; i++) {
            userBalance[i].token = _tokens[i];
            userBalance[i].balance = _balances[_tokens[i]][user];
        }
        address strategyMapAddress = moduleMap.getModuleAddress(Modules.StrategyMap);
        for (uint256 i = 0; i < _strategies.length; i++) {
            IStrategyMap.Token[] memory strategyTokens = IStrategyMap(strategyMapAddress)
                .getStrategy(_strategies[i])
                .tokens;

            strategyBalance[i].tokens = new IStrategyMap.GeneralBalance[](strategyTokens.length);
            strategyBalance[i].strategyID = _strategies[i];
            for (uint256 j = 0; j < strategyTokens.length; j++) {
                strategyBalance[i].tokens[j].token = strategyTokens[j].token;
                strategyBalance[i].tokens[j].balance = _userStrategyBalances[user][_strategies[i]][
                    strategyTokens[j].token
                ];
            }
        }
    }

    function _enterStrategy(
        uint256 strategyId,
        address user,
        TokenMovement[] calldata tokens,
        bool migration
    ) internal {
        require(tokens.length > 0, "tokens required");

        for (uint256 i = 0; i < tokens.length; i++) {
            require(tokens[i].token != address(0), "invalid token");
            require(tokens[i].amount > 0, "invalid amount");
            if (!migration) {
                require(userDeployableBalance(tokens[i].token, user) >= tokens[i].amount, "insufficient funds");
            } else {
                _balances[tokens[i].token][user] += tokens[i].amount;
            }

            _userStrategyBalances[user][strategyId][tokens[i].token] += tokens[i].amount;
            _deployedBalances[user][tokens[i].token] += tokens[i].amount;
        }

        emit EnterStrategy(strategyId, user, tokens);
    }

    function migrateUser(uint256 strategyId, MigrateStrategy[] calldata users) external override onlyController {
        IStrategyMap strategyMap = IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap));
        for (uint256 i = 0; i < users.length; i++) {
            _enterStrategy(strategyId, users[i].user, users[i].tokens, true);
            strategyMap.increaseTokenBalance(strategyId, users[i].tokens);
        }
    }

    function _transferIn(
        address from,
        address token,
        uint256 amount
    ) private returns (uint256 actualAmount) {
        address kernel = moduleMap.getModuleAddress(Modules.Kernel);

        IERC20MetadataUpgradeable erc20 = IERC20MetadataUpgradeable(token);
        // Get the balance before the transfer
        uint256 beforeBalance = erc20.balanceOf(kernel);

        // Transfer the tokens from the source to the Kernel
        erc20.safeTransferFrom(from, kernel, amount);

        // Get the balance after the transfer
        actualAmount = erc20.balanceOf(kernel) - beforeBalance;
    }
}
","keccak256":"0x3aab9507352c920eae6ff419d82cfba9e023f576e8e0db22edb9f35b9743df41","license":"GPL-2.0"},"contracts/interfaces/IAMMIntegration.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

interface IAMMIntegration {
    struct Pool {
        address tokenA;
        address tokenB;
        uint256 positionID; // Used for Uniswap V3
    }

    /// Events
    event CreatePool(address indexed tokenA, address indexed tokenB, uint32 poolID);

    event HarvestYieldError(uint32 indexed poolID);

    /// @dev IMPORTANT: poolID must start at 1 for all amm integrations. A poolID of 0 is used to designate a non amm integration.

    /// @param token The address of the deposited token
    /// @param amount The amount of token being deposited
    /// @param poolID  The id of the pool to deposit into
    function deposit(
        address token,
        uint256 amount,
        uint32 poolID
    ) external;

    /// @param token  the token to withdraw
    /// @param amount The amount of token in the pool to withdraw
    /// @param poolID  the pool to withdraw from
    function withdraw(
        address token,
        uint256 amount,
        uint32 poolID
    ) external;

    /// @dev Deploys all the tokens for the specified pools
    function deploy(uint32 poolID) external;

    /// @dev Harvests token yield from the integration
    function harvestYield() external;
}
