pragma solidity 0.8.4;

interface IEtherRewards {
    // structs
    struct UserInfo {
        // rewardDebt uses MCV2 technology, it can be negative!
        int256 rewardDebt;
        uint256 claimedRewards;
    }

    struct StrategyInfo {
        uint256 rewardPerShare; // rewardPerShare * 1e12
        uint256 totalRewards;
    }

    /// @param strategyId The strategy the user entered
    /// @param amount The amount of token entered by the user
    /// @param user The address of the user
    function increaseUserRewards(
        uint256 strategyId,
        uint256 amount,
        address user
    ) external;

    /// @param strategyId The strategy the user exited
    /// @param amount The amount of token exited by the user
    /// @param user The address of the user
    function decreaseUserRewards(
        uint256 strategyId,
        uint256 amount,
        address user
    ) external;

    /// @param strategyId The strategy to the rewards are for
    /// @param ethRewardsAmount The amount of Ether rewards to add
    function increaseEthRewards(uint256 strategyId, uint256 ethRewardsAmount) external;

    /// @param user The address of the user
    /// @param ids An array of strategyIds to claim rewards for
    /// @return ethRewards The amount of Ether claimed
    function claimEthRewards(address user, uint256[] calldata ids) external returns (uint256 ethRewards);

    /// @param strategyId The ID of the strategy
    /// @param user The address of the user
    /// @return ethRewards The amount of Ether claimed
    function getUserStrategyRewards(uint256 strategyId, address user) external view returns (uint256 ethRewards);

    /// @param user The address of the user
    /// @return ethRewards The amount of Ether claimed
    function getUserEthRewards(address user) external view returns (uint256 ethRewards);

    /// @return The total value of ETH claimed by users
    function getTotalClaimedEthRewards() external view returns (uint256);

    /// @return The total value of ETH claimed by a user
    function getTotalUserClaimedEthRewards(address user) external view returns (uint256);

    /// @return The total amount of Ether rewards
    function getEthRewards() external view returns (uint256);
}
