","keccak256":"0x0004699ba89fa356eb0d58d815b42dcf52eccd2765e95256631b838bce2556bc","license":"MIT"},"contracts/core/BiosEmitterV0.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

import "../interfaces/IIntegrationMap.sol";
import "../interfaces/IUserPositions.sol";
import "../interfaces/IBiosEmitter.sol";
import "../libraries/SignedSafeMath.sol";

contract BiosEmitterV0 is Initializable, ModuleMapConsumer, Controlled, IBiosEmitter {
    using SignedSafeMath for int256;
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable; // IERC20MetadataUpgradeable

    /// @notice Info of each MCV2 pool.
    PoolInfo[] public poolInfo;
    /// @notice token address mapping to pool index
    mapping(address => uint256) public pid;
    mapping(address => bool) private addedPools;

    /// @notice Info of each user that stakes LP tokens.
    // pool => user => rewardDebt
    mapping(uint256 => mapping(address => int256)) public rewardDebt;

    uint256 private bios_per_block;
    uint256 private constant ACC_BIOS_PRECISION = 1e12;

    /// @dev Total allocation points. Must be the sum of all allocation points in all pools.
    uint256 public totalAllocPoint;

    event BiosEmitterHarvest(address indexed from, uint256 indexed pid, uint256 amount, address indexed to);
    event EmergencyWithdraw(address indexed user, uint256 indexed pid, uint256 amount, address indexed to);
    event MigratedUserPosition(address indexed user, uint256 amount);
    event UpdatedUserPosition(address indexed user, uint256 amount);
    event LogBiosPerBlock(uint256 biosPerBlock);
    event LogPoolAddition(uint256 indexed pid, uint256 allocPoint);
    event LogSetPool(uint256 indexed pid, uint256 allocPoint);
    event LogUpdatePool(uint256 indexed pid, uint64 lastRewardBlock, uint256 lpSupply, uint256 accBiosPerShare);
    event LogPendingBios(uint256 _pendingBios);

    modifier validBiosPerBlock(uint256 _value) {
        require(
            (_value > 0) && (_value <= 1e20),
            "BiosEmitter::validBiosPerBlock: Invalid value. bios_per_block must be greater than 0 or less than or equal to 1e20"
        );
        _;
    }

    modifier exists(address _token) {
        require(addedPools[_token], "BiosEmitter::exists: Pool does not exist");
        _;
    }

    modifier doesNotExist(address _token) {
        require(!addedPools[_token], "BiosEmitter::doesNotExist: Pool alreaady exists");
        _;
    }

    function initialize(
        address[] memory controllers_,
        address moduleMap_,
        uint256 _bios_per_block
    ) public initializer validBiosPerBlock(_bios_per_block) {
        __Controlled_init(controllers_, moduleMap_);
        bios_per_block = _bios_per_block;
        emit LogBiosPerBlock(_bios_per_block);
    }

    /// @notice Set the number of bios per block
    /// @param _bios_per_block # of BIOS per block
    function setBiosPerBlock(uint256 _bios_per_block) external onlyOwner validBiosPerBlock(_bios_per_block) {
        bios_per_block = _bios_per_block;
        emit LogBiosPerBlock(_bios_per_block);
    }

    function addPools(address[] memory _tokens, uint256[] memory _allocPoints) external onlyOwner {
        for (uint256 i = 0; i < _tokens.length; i++) {
            addPool(_tokens[i], _allocPoints[i]);
        }
    }

    /// @notice Add a new LP to the pool. Can only be called by the owner.
    /// DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    /// @param _token Address of the LP ERC-20 token.
    /// @param _allocPoint AP of the new pool.
    function addPool(address _token, uint256 _allocPoint) public doesNotExist(_token) onlyOwner {
        uint256 lastRewardBlock = block.number;
        totalAllocPoint += _allocPoint;
        addedPools[_token] = true;

        poolInfo.push(
            PoolInfo({
                allocPoint: uint64(_allocPoint),
                lastRewardBlock: uint64(lastRewardBlock),
                accBiosPerShare: 0,
                initialized: true
            })
        );

        pid[_token] = poolInfo.length - 1;

        emit LogPoolAddition(poolInfo.length - 1, _allocPoint);
    }

    function setPools(address[] memory _tokens, uint256[] memory _allocPoints) external onlyOwner {
        for (uint256 i = 0; i < _tokens.length; i++) {
            setPool(_tokens[i], _allocPoints[i]);
        }
    }

    /// @notice Update the given pool's BIOS allocation point. Can only be called by the owner.
    /// @param _token The address of the token.
    /// @param _allocPoint New AP of the pool.
    function setPool(address _token, uint256 _allocPoint) public exists(_token) onlyOwner {
        uint256 _pid = getPoolId(_token);
        // totalAllocPoint = totalAllocPoint.sub(poolInfo[_pid].allocPoint).add(_allocPoint);
        totalAllocPoint = totalAllocPoint - poolInfo[_pid].allocPoint + _allocPoint;
        poolInfo[_pid].allocPoint = uint64(_allocPoint);
        emit LogSetPool(_pid, _allocPoint);
    }

    function _getLpSupply(address _token) private view returns (uint256 lpSupply) {
        lpSupply = IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions)).totalTokenBalance(_token);
    }

    function _getUserBalance(address _token, address _user) private view returns (uint256 userBalance) {
        userBalance = IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions)).userTokenBalance(_token, _user);
    }

    /// @notice View function to see pending BIOS on frontend.
    /// @param _user Address of user.
    /// @return pending BIOS reward for a given user.
    function pendingBios(address _token, address _user) external view exists(_token) returns (uint256 pending) {
        uint256 _pid = getPoolId(_token);
        PoolInfo memory pool = poolInfo[_pid];

        uint256 accBiosPerShare = pool.accBiosPerShare;

        uint256 lpSupply = _getLpSupply(_token);

        if (block.number > pool.lastRewardBlock && lpSupply != 0) {
            uint256 blocks = block.number - pool.lastRewardBlock;
            uint256 biosReward = (blocks * bios_per_block * pool.allocPoint) / totalAllocPoint;
            accBiosPerShare += ((biosReward * ACC_BIOS_PRECISION) / lpSupply);
        }
        pending = (int256((_getUserBalance(_token, _user) * accBiosPerShare) / ACC_BIOS_PRECISION) -
            rewardDebt[_pid][_user]).toUInt256();
    }

    /// @notice Update reward variables for all pools. Be careful of gas spending!
    /// @param _tokens Pool IDs of all to be updated. Make sure to update all active pools.
    function massUpdatePools(address[] calldata _tokens) external {
        uint256 len = _tokens.length;
        for (uint256 i = 0; i < len; ++i) {
            updatePool(_tokens[i]);
        }
    }

    /// @notice Update reward variables of the given pool.
    /// @notice IMPORTANT! If being called as part of a UserPositions update, it must be called before UserPositions state has been updated!
    /// @return pool Returns the pool that was updated.
    function updatePool(address _token) public override exists(_token) returns (PoolInfo memory pool) {
        uint256 _pid = getPoolId(_token);
        pool = poolInfo[_pid];
        if (block.number > pool.lastRewardBlock) {
            uint256 lpSupply = _getLpSupply(_token);
            if (lpSupply > 0) {
                uint256 blocks = block.number - pool.lastRewardBlock;
                uint256 biosReward = (blocks * bios_per_block * pool.allocPoint) / totalAllocPoint;
                pool.accBiosPerShare += uint128((biosReward * ACC_BIOS_PRECISION) / lpSupply);
            }
            pool.lastRewardBlock = uint64(block.number);
            poolInfo[_pid] = pool;

            emit LogUpdatePool(_pid, pool.lastRewardBlock, lpSupply, pool.accBiosPerShare);
        }
    }

    /// @notice Deposit LP tokens to MC for BIOS allocation. MUST be called before UserPositions is updated!
    /// @param _amount LP token amount to deposit.
    /// @param _depositor The receiver of `amount` deposit benefit.
    function deposit(
        address _token,
        uint256 _amount,
        address _depositor
    ) public override exists(_token) onlyController {
        uint256 _pid = getPoolId(_token);
        PoolInfo memory pool = updatePool(_token);

        // Effects
        // user.rewardDebt = user.rewardDebt.add(int256(_amount.mul(pool.accBiosPerShare) / ACC_BIOS_PRECISION));
        rewardDebt[_pid][_depositor] += (int256((_amount * pool.accBiosPerShare) / ACC_BIOS_PRECISION));
    }

    /// @notice Withdraw LP tokens from MC. MUST be called before UserPositions is updated!
    /// @param _amount LP token amount to withdraw.
    /// @param _withdrawer Receiver of the LP tokens.
    function withdraw(
        address _token,
        uint256 _amount,
        address _withdrawer
    ) public override exists(_token) onlyController {
        uint256 _pid = getPoolId(_token);
        PoolInfo memory pool = updatePool(_token);

        // Effects
        rewardDebt[_pid][_withdrawer] -= (int256((_amount * pool.accBiosPerShare) / ACC_BIOS_PRECISION));
    }

    /// @notice Harvest proceeds for transaction sender to `to`.
    /// @param _harvester Receiver of BIOS rewards.
    function harvest(address _token, address _harvester)
        public
        override
        exists(_token)
        onlyController
        returns (uint256)
    {
        uint256 _pendingBios = _harvest(_token, _harvester);
        // Interactions
        if (_pendingBios > 0) {
            IERC20MetadataUpgradeable bios = IERC20MetadataUpgradeable(
                IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress()
            );
            bios.safeTransfer(_harvester, _pendingBios); // release rewards to user
        }
        return _pendingBios;
    }

    function harvestAll(address _harvester) external override onlyController returns (uint256) {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        uint256 length = integrationMap.getTokenAddressesLength();
        uint256 totalHarvested;
        for (uint256 i; i < length; ++i) {
            address token = integrationMap.getTokenAddress(i);
            if (addedPools[token]) {
                totalHarvested += _harvest(token, _harvester);
            }
        }
        if (totalHarvested > 0) {
            IERC20MetadataUpgradeable bios = IERC20MetadataUpgradeable(
                IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress()
            );
            bios.safeTransfer(_harvester, totalHarvested); // release rewards to user
        }
        return totalHarvested;
    }

    function _harvest(address _token, address _harvester) internal returns (uint256 amount) {
        uint256 _pid = getPoolId(_token);
        PoolInfo memory pool = updatePool(_token);
        uint256 userBalance = _getUserBalance(_token, _harvester);
        // require(userBalance > 0, "BiosEmitter::harvest: Cannot harvest because the user's reported balance is zero");
        if (userBalance == 0) return 0;

        int256 accumulatedBios = int256((userBalance * pool.accBiosPerShare) / ACC_BIOS_PRECISION);
        uint256 _pendingBios = (accumulatedBios - rewardDebt[_pid][_harvester]).toUInt256();

        // Effects
        // user.amount = user.amount.sub(_amount);
        rewardDebt[_pid][_harvester] = accumulatedBios;

        emit BiosEmitterHarvest(address(this), _pid, _pendingBios, _harvester);

        return _pendingBios;
    }

    // /// @notice Withdraw LP tokens from MC and harvest proceeds for transaction sender to `to`.
    // /// @param _token LP token address.
    // /// @param _amount LP token amount to withdraw.
    // /// @param _harvester Receiver of the LP tokens and BIOS rewards.
    // function withdrawAndHarvest(
