pragma solidity 0.8.4;

import "../interfaces/IEtherRewards.sol";
import "../interfaces/IIntegrationMap.sol";
import "../interfaces/IUserPositions.sol";
import "../interfaces/IStrategyMap.sol";
import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

// https://solmaz.io/2019/02/24/scalable-reward-changing/
// https://github.com/sushiswap/sushiswap/blob/canary/contracts/MasterChefV2.sol
contract EtherRewards is Initializable, ModuleMapConsumer, Controlled, IEtherRewards {
    uint256 private totalEthRewards;
    uint256 private totalClaimedEthRewards;
    mapping(address => uint256) private totalUserClaimedEthRewards;

    // user => strategyId => UserInfo
    mapping(address => mapping(uint256 => UserInfo)) private users;

    // strategyId => StrategyInfo
    mapping(uint256 => StrategyInfo) private strategies;

    // Used for accounting precision
    uint256 private constant ACC_PRECISION = 1e12;

    /// @param controllers_ The array of controllers for this contract
    /// @param moduleMap_ The address of the ModuleMap contract
    function initialize(address[] memory controllers_, address moduleMap_) external initializer {
        __Controlled_init(controllers_, moduleMap_);
    }

    /// @param strategyId Strategy the user entered into
    /// @param amount Amount of token entered into strategy
    /// @param user The address of the user
    function increaseUserRewards(
        uint256 strategyId,
        uint256 amount,
        address user
    ) external override onlyController {
        // Increase reward debt so users don't get back credited
        users[user][strategyId].rewardDebt += int256((amount * strategies[strategyId].rewardPerShare) / ACC_PRECISION);
    }

    /// @param strategyId Strategy the user entered into
    /// @param amount Amount of token entered into strategy
    /// @param user The address of the user
    function decreaseUserRewards(
        uint256 strategyId,
        uint256 amount,
        address user
    ) external override onlyController {
        // Decrease reward debt so users don't lose rewards already earned
        users[user][strategyId].rewardDebt -= int256((amount * strategies[strategyId].rewardPerShare) / ACC_PRECISION);
    }

    function donate(uint256 strategyId, uint256 ethRewardsAmount) external payable {
        require(ethRewardsAmount > 0 && msg.value == ethRewardsAmount, "need eth");
        _increaseEthRewards(strategyId, ethRewardsAmount);
    }

    /// @dev strategyId = 0 for BIOS PFA!
    /// @param strategyId The strategyId of the strategy to increase rewards for
    /// @param ethRewardsAmount The amount of Ether rewards to add
    function increaseEthRewards(uint256 strategyId, uint256 ethRewardsAmount) external override onlyController {
        _increaseEthRewards(strategyId, ethRewardsAmount);
    }

    function _increaseEthRewards(uint256 strategyId, uint256 ethRewardsAmount) internal {
        StrategyInfo storage strategy = strategies[strategyId];

        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));

        // BIOS is special and just uses deposits, not strategy balance
        uint256 tokenTotalAmount = strategyId == 0
            ? IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions)).totalTokenBalance(
                integrationMap.getBiosTokenAddress()
            )
            : IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).getStrategyTokenBalance(
                strategyId,
                _getStrategyToken(strategyId)
            );
        require(tokenTotalAmount > 0, "EtherRewards::tokenNotDeposited");

        // Update strategy rate and total
        strategy.rewardPerShare += (ethRewardsAmount * ACC_PRECISION) / tokenTotalAmount;
        strategy.totalRewards += ethRewardsAmount;

        // Update global rewards
        totalEthRewards += ethRewardsAmount;
    }

    /// @param user The address of the user
    /// @param ids An array of strategyIds to claim rewards for
    /// @return ethRewards The amount of Ether claimed
    function claimEthRewards(address user, uint256[] calldata ids)
        external
        override
        onlyController
        returns (uint256 ethRewards)
    {
        for (uint256 i; i < ids.length; ++i) {
            ethRewards += _claimStrategyRewards(ids[i], user);
        }
    }

    /// @param strategyId The strategy to claim rewards from
    /// @param user The address of the user
    /// @return ethRewards The amount of Ether claimed
    function _claimStrategyRewards(uint256 strategyId, address user) private returns (uint256) {
        (uint256 ethRewards, uint256 userAmount) = _getUserStrategyRewards(strategyId, user);

        if (ethRewards > 0) {
            // User accounting
            users[user][strategyId].rewardDebt = int256(
                (strategies[strategyId].rewardPerShare * userAmount) / ACC_PRECISION
            );

            // Global accounting
            totalClaimedEthRewards += ethRewards;
            totalUserClaimedEthRewards[user] += ethRewards;
        }

        return ethRewards;
    }

    /// @param strategyId The ID of the strategy
    /// @param user The address of the user
    /// @return ethRewards The amount of claimable Ether
    function getUserStrategyRewards(uint256 strategyId, address user)
        external
        view
        override
        returns (uint256 ethRewards)
    {
        (ethRewards, ) = _getUserStrategyRewards(strategyId, user);
    }

    function _getUserStrategyRewards(uint256 strategyId, address user)
        private
        view
        returns (uint256 ethRewards, uint256 userAmount)
    {
        UserInfo memory userInfo = users[user][strategyId];
        IUserPositions userPositions = IUserPositions(moduleMap.getModuleAddress(Modules.UserPositions));

        // Fetch balances. BIOS is special and has no strategy
        if (strategyId == 0) {
            userAmount = userPositions.userTokenBalance(
                IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress(),
                user
            );
        } else {
            userAmount = userPositions.getUserStrategyBalanceByToken(strategyId, _getStrategyToken(strategyId), user);
        }

        ethRewards = _subtractDebt(
            ((strategies[strategyId].rewardPerShare * userAmount) / ACC_PRECISION),
            userInfo.rewardDebt
        );
    }

    /// @param user The address of the user
    /// @return ethRewards The amount of Ether claimed
    function getUserEthRewards(address user) external view override returns (uint256) {
        IStrategyMap strategyMap = IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap));

        uint256 totalRewards;

        // We purposefully check 0 for BIOS (PFA)
        for (uint256 i; i <= strategyMap.idCounter(); ++i) {
            (uint256 rewards, ) = _getUserStrategyRewards(i, user);
            totalRewards += rewards;
        }

        return totalRewards;
    }

    /// @return The total value of ETH claimed by users
    function getTotalClaimedEthRewards() external view override returns (uint256) {
        return totalClaimedEthRewards;
    }

    /// @return The total value of ETH claimed by a user
    function getTotalUserClaimedEthRewards(address account) external view override returns (uint256) {
        return totalUserClaimedEthRewards[account];
    }

    function getTotalUserClaimedEthByStrategy(address user, uint256[] calldata ids)
        external
        view
        returns (uint256[] memory)
    {
        uint256[] memory claimed = new uint256[](ids.length);
        for (uint256 i; i < ids.length; ++i) {
            claimed[i] = users[user][ids[i]].claimedRewards;
        }
        return claimed;
    }

    /// @return The total amount of Ether rewards
    function getEthRewards() external view override returns (uint256) {
        return totalEthRewards;
    }

    function getStrategyInfo(uint256 strategyId) external view returns (StrategyInfo memory) {
        return strategies[strategyId];
    }

    function getUserInfo(uint256 strategyId, address user) external view returns (UserInfo memory) {
        return users[user][strategyId];
    }

    function _getStrategyToken(uint256 strategyId) internal view returns (address) {
        if (strategyId == 0) {
            return IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress();
        } else {
            return
                IStrategyMap(moduleMap.getModuleAddress(Modules.StrategyMap)).getStrategy(strategyId).tokens[0].token;
        }
    }

    /// @notice Helper function for math between uint and int
    function _subtractDebt(uint256 rewards, int256 debt) private pure returns (uint256) {
        if (debt < 0) {
            return rewards + uint256(debt * -1);
        } else {
            return rewards - uint256(debt);
        }
    }
}
