        }
    }

    /// @param token The address of the deposited token
    /// @return The amount of the deposited token that can be withdrawn from the vault
    function getVaultWithdrawableAmount(address token) public view returns (uint256) {
        IERC20MetadataUpgradeable shareToken = IERC20MetadataUpgradeable(getVaultAddress(token));

        return (getPricePerShare(token) * shareToken.balanceOf(address(this))) / (10**shareToken.decimals());
    }

    /// @dev Returns total amount of pending yield for the specified token in Yearn
    /// @param token The of the token to check for available yield
    /// @return Amount of yield available for harvest
    function getPendingYield(address token) external view override returns (uint256) {
        return getAvailableYieldInShares(token);
    }
}
