pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./ModuleMapConsumer.sol";
import "../interfaces/IKernel.sol";

abstract contract Controlled is Initializable, ModuleMapConsumer {
    // controller address => is a controller
    mapping(address => bool) internal _controllers;
    address[] public controllers;

    function __Controlled_init(address[] memory controllers_, address moduleMap_) internal onlyInitializing {
        require(moduleMap_ != address(0), "bad address");
        for (uint256 i; i < controllers_.length; ++i) {
            require(controllers_[i] != address(0), "bad address");
        }
        for (uint256 i; i < controllers_.length; ++i) {
            _controllers[controllers_[i]] = true;
        }
        controllers = controllers_;
        __ModuleMapConsumer_init(moduleMap_);
    }

    function addController(address controller) external onlyOwner {
        _controllers[controller] = true;
        bool added;
        for (uint256 i; i < controllers.length; ++i) {
            if (controller == controllers[i]) {
                added = true;
            }
        }
        if (!added) {
            controllers.push(controller);
        }
    }

    modifier onlyOwner() {
        require(
            IKernel(moduleMap.getModuleAddress(Modules.Kernel)).isOwner(msg.sender),
            "Controlled::onlyOwner: Caller is not owner"
        );
        _;
    }

    modifier onlyManager() {
        require(
            IKernel(moduleMap.getModuleAddress(Modules.Kernel)).isManager(msg.sender),
            "Controlled::onlyManager: Caller is not manager"
        );
        _;
    }

    modifier onlyController() {
        require(_controllers[msg.sender], "Controlled::onlyController: Caller is not controller");
        _;
    }

    function isController(address controller) external view returns (bool status) {
        status = _controllers[controller];
    }

    function getAllControllers() external view returns (address[] memory currentControllers) {
        return controllers;
    }
}
