pragma solidity 0.8.4;

interface ISwapManager {
    /**
     * @dev Executes swap only for amountIn which will not significantly imbalance the pool and returns this amountIn
     * @param _tokenIn The address of the input token
     * @param _tokenOut The address of the output token
     * @param _ratioIn The left part of the ratio tokenA : tokenB which we will stick to
     * @param _ratioOut The right part of the ratio tokenA : tokenB which we will stick to
     * @param _recipient The address of the token out recipient
     * @param _desiredAmountIn Requested amountIn
     * @param _desiredMinAmountOut Like usual minAmountOut, but will decrease proportionally with decreasing _desiredAmountIn
     * @return spendedAmountIn_ <= _desiredAmountOut spended for the swap
     **/
    function gentleSwapExactIn(
        address _tokenIn,
        address _tokenOut,
        uint32 _ratioIn,
        uint32 _ratioOut,
        address _recipient,
        uint256 _desiredAmountIn,
        uint256 _desiredMinAmountOut
    ) external returns (uint256 spendedAmountIn_);

    /**
     * @param _tokenIn The address of the input token
     * @param _tokenOut The address of the output token
     * @param _recipient The address of the token out recipient
     * @param _amountIn The exact amount of the input to swap
     * @param _minAmountOut The minimum amount of _tokenOut to receive
     * @return tradeSuccess_ Indicates whether the swap succeeded
     **/
    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _minAmountOut
    ) external returns (bool tradeSuccess_);

    /**
     * @param _tokenIn The address of the input token
     * @param _tokenOut The address of the output token
     * @param _recipient The address of the token out recipient
     * @param _amountIn The exact amount of the input to swap
     * @return tradeSuccess_ Indicates whether the swap succeeded
     **/
    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn
    ) external returns (bool tradeSuccess_);

    /**
     * @notice Swaps all WETH held in this contract for BIOS and sends to the kernel
     * @param ratioX1000 BIOS/WETH ratio for front runner protection
     * @return Bool indicating whether the trade succeeded
     **/
    function biosBuyBack(uint256 ratioX1000) external returns (bool);

    /**
     * @param _tokenIn The address of the input token
     * @param _tokenOut The address of the output token
     * @param _amountIn The exact amount of the input to swap
     * @return isPoolExist_ Can we swap these tokens via added traders?
     * @return bestPrice_ Best amount out from all traders
     **/
    function getBestAmountOut(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external view returns (bool isPoolExist_, uint256 bestPrice_);
}
