pragma solidity 0.8.4;

import "../interfaces/IAMMIntegration.sol";
import "../interfaces/ISushiSwapFactory.sol";
import "../interfaces/ISushiSwapPair.sol";
import "../interfaces/IERC20.sol";

library AMMLibrary {
    function getDeployAmounts(
        uint256 balanceA,
        uint256 balanceB,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountA, uint256 amountB) {
        if (reserveA > reserveB) {
            uint256 k = (reserveA * 1000) / reserveB;
            uint256 balanceBA = (balanceB * k) / 1000;

            if (balanceA < balanceBA) {
                amountA = balanceA;
                amountB = (balanceA * 1000) / k;
            } else if (balanceBA < balanceA) {
                amountA = (balanceB * k) / 1000;
                amountB = balanceB;
            } else {
                amountA = balanceA;
                amountB = balanceB;
            }
        } else if (reserveA < reserveB) {
            uint256 k = (reserveB * 1000) / reserveA;
            uint256 balanceAB = (balanceA * k) / 1000;

            if (balanceB < balanceAB) {
                amountA = (balanceB * 1000) / k;
                amountB = balanceB;
            } else if (balanceAB < balanceB) {
                amountA = balanceA;
                amountB = (balanceA * k) / 1000;
            } else {
                amountA = balanceA;
                amountB = balanceB;
            }
        }
    }

    function getExcessTokenAmounts(
        IAMMIntegration.Pool memory pool,
        uint256 balanceA,
        uint256 balanceB,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (address swapToken, uint256 excessAmountToSwap) {
        if (reserveA > reserveB) {
            uint256 k = (reserveA * 1000) / reserveB;
            uint256 balanceBA = (balanceB * k) / 1000;

            if (balanceA < balanceBA) {
                // excess tokenB
                excessAmountToSwap = (balanceB - ((balanceA * 1000) / k)) / 2;
                swapToken = pool.tokenB;
            } else if (balanceBA < balanceA) {
                // excess tokenA
                excessAmountToSwap = (balanceA - balanceBA) / 2;
                swapToken = pool.tokenA;
            }
        } else if (reserveA < reserveB) {
            uint256 k = (reserveB * 1000) / reserveA;
            uint256 balanceAB = (balanceA * k) / 1000;

            if (balanceAB < balanceB) {
                // excess tokenB
                excessAmountToSwap = (balanceB - balanceAB) / 2;
                swapToken = pool.tokenB;
            } else if (balanceB < balanceAB) {
                // excess tokenA
                excessAmountToSwap = (balanceA - ((balanceB * 1000) / k)) / 2;
                swapToken = pool.tokenA;
            }
        }
    }

    function getAmountOfTokensInPool(
        uint256 lpAmount,
        uint256 totalSupply,
        uint256 balanceA,
        uint256 balanceB
    ) internal pure returns (uint256 amountA, uint256 amountB) {
        uint256 sharePercent = (lpAmount * 10000000000) / totalSupply;
        amountA = (balanceA * sharePercent) / 10000000000;
        amountB = (balanceB * sharePercent) / 10000000000;
    }

    function sortTokens(address tokenA, address tokenB) internal pure returns (address token0, address token1) {
        require(tokenA != tokenB, "Identical token addresses");
        (token0, token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), "Zero address");
    }

    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountOut) {
        require(amountIn > 0, "amountIn must be greater than zero");
        require(reserveIn > 0 && reserveOut > 0, "No liquidity in pool reserves");
        uint256 amountInWithFee = amountIn * (997);
        uint256 numerator = amountInWithFee * (reserveOut);
        uint256 denominator = reserveIn * 1000 + amountInWithFee;
        amountOut = numerator / denominator;
    }

    function getRebalanceAmounts(
        address swapToken,
        IAMMIntegration.Pool memory pool,
        uint256 excessAmountToSwap,
        uint256 maxSellTokenA,
        uint256 maxSellTokenB
    ) internal pure returns (uint256 swapAmount, address targetToken) {
        if (swapToken == pool.tokenA) {
            targetToken = pool.tokenB;
            swapAmount = excessAmountToSwap < maxSellTokenA ? excessAmountToSwap : maxSellTokenA;
        } else {
            targetToken = pool.tokenA;
            swapAmount = excessAmountToSwap < maxSellTokenB ? excessAmountToSwap : maxSellTokenB;
        }
    }

    function checkPriceCeiling(
        address pairAddress,
        uint256 reserveA,
        uint256 reserveB,
        uint256 ratioX1000
    ) internal pure {
        require(pairAddress != address(0), "TraderJoeIntegration:BadPair");

        uint256 currentRatio;

        if (reserveA < reserveB) {
            currentRatio = (reserveB * 1000) / reserveA;
        } else if (reserveB < reserveA) {
            currentRatio = (reserveA * 1000) / reserveB;
        } else {
            currentRatio = 1000;
        }

        // limit to 1% off
        if (currentRatio > ratioX1000) {
            require(((currentRatio - ratioX1000) * 100) / ratioX1000 < 1, "CeilingLimitReached");
        } else if (ratioX1000 > currentRatio) {
            require(((ratioX1000 - currentRatio) * 100) / ratioX1000 < 1, "CeilingLimitReached");
        }
    }

    function getLiquidityToWithdraw(
        address tokenAddress,
        uint256 amount,
        uint256 availableLiquidity,
        uint256 reserve0,
        uint256 reserve1,
        uint256 totalSupply,
        IAMMIntegration.Pool memory pool
    ) internal pure returns (uint256 liquidityToWithdraw) {
        uint256 tokensPerLP;

        if (tokenAddress == pool.tokenA) {
            tokensPerLP = totalSupply == 0 ? 0 : (reserve0 * 1e18) / totalSupply;
        } else {
            tokensPerLP = totalSupply == 0 ? 0 : (reserve1 * 1e18) / totalSupply;
        }

        if (tokensPerLP == 0) {
            return 0;
        }

        liquidityToWithdraw = ((amount / 2) * 1e18) / tokensPerLP;

        if (liquidityToWithdraw > availableLiquidity) {
            liquidityToWithdraw = availableLiquidity;
        }
    }
}
