pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";
import "../interfaces/IAMMIntegration.sol";
import "../interfaces/IIntegrationMap.sol";
import "../interfaces/ISwapManager.sol";
import "../interfaces/IERC20.sol";
import "../libraries/AMMLibrary.sol";
import "../interfaces/ISolidlyGauge.sol";
import "../interfaces/ISolidlyPairV1.sol";
import "../interfaces/ISolidlyRouterV1.sol";
import "../interfaces/ISolidlyFactoryV1.sol";

contract MaiaDaoHermesIntegration is Controlled, IAMMIntegration {
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    address public constant rewardToken = 0xb27BbeaACA2C00d6258C3118BAB6b5B6975161c8;

    address factoryAddress;
    address swapRouterAddress;
    address wnativeAddress;
    uint32 public poolCount;
    uint24 private constant SLIPPAGE_DENOMINATOR = 1_000_000;
    uint24 public slippageNumerator;

    // poolId => Token => Amount currently available to deploy
    mapping(uint32 => mapping(address => uint256)) public balances;
    // Pool ID => Pool data
    mapping(uint32 => Pool) public pools;
    // Pool ID => whether it's a solidly stable pair
    mapping(uint32 => bool) public stablePools;
    // Pool id => yield available
    mapping(uint32 => uint256) public yieldBalances;
    // harvest yield (weth) per pool (harvested by YieldManager)
    mapping(uint32 => uint256) public harvestedYield;
    // poolId => Gauge
    mapping(uint32 => ISolidlyGauge) private stakings;

    event TokensReceived(uint256 amount);
    event LPTokensAcquired(uint256 amount);
    event WithdrawnFromPool(uint256 poolId, uint256 amount);
    event YieldReceived(uint256 amount);
    event PoolRebalanced(
        uint256 poolId,
        address tokenSold,
        uint256 amountSold,
        address tokenBought,
        uint256 amountBought
    );

    function initialize(
        address[] memory controllers_,
        address moduleMap_,
        address factoryAddress_,
        address swapRouterAddress_,
        uint24 slippageNumerator_
    ) external initializer {
        require(factoryAddress_ != address(0), "bad address");
        require(swapRouterAddress_ != address(0), "bad address");

        __Controlled_init(controllers_, moduleMap_);
        factoryAddress = factoryAddress_;
        swapRouterAddress = swapRouterAddress_;
        slippageNumerator = slippageNumerator_;
        wnativeAddress = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress();
    }

    function deposit(
        address token,
        uint256 amount,
        uint32 poolId
    ) external override onlyController {
        balances[poolId][token] += amount;
    }

    /// @param token The address of the deposited token
    /// @param amount The amount of the token being deposited
    function incrementBalance(
        uint32 poolId,
        address token,
        uint256 amount
    ) external onlyManager {
        balances[poolId][token] += amount;
    }

    function decrementBalance(
        uint32 poolId,
        address token,
        uint256 amount
    ) external onlyManager {
        balances[poolId][token] -= amount;
    }

    /// @param token  the token to withdraw
    /// @param amount The amount of token in the pool to withdraw
    /// @param poolId  the pool to withdraw from
    function withdraw(
        address token,
        uint256 amount,
        uint32 poolId
    ) external override onlyController {
        require(balances[poolId][token] >= amount, "insufficient integration balances");
        balances[poolId][token] -= amount;
        IERC20MetadataUpgradeable(token).safeTransfer(moduleMap.getModuleAddress(Modules.Kernel), amount);
    }

    function withdraw(
        address token,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000
    ) external onlyController {
        if (amount <= balances[poolId][token]) {
            balances[poolId][token] -= amount;
            IERC20MetadataUpgradeable(token).safeTransfer(moduleMap.getModuleAddress(Modules.Kernel), amount);
        } else {
            _withdraw(token, amount, poolId, ratioX1000, true);
        }
        require(balances[poolId][token] >= amount, "insufficient integration balances");
    }

    function manualWithdraw(
        address token,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000
    ) external onlyManager returns (uint256 amountToWithdraw) {
        return _withdraw(token, amount, poolId, ratioX1000, false);
    }

    function _withdraw(
        address token,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000,
        bool sendToKernel
    ) internal returns (uint256 amountWithdrawn) {
        address biosAddress = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress();
        Pool memory pool = pools[poolId];
        bool stable = stablePools[poolId];
        if (ratioX1000 > 0) {
            checkPriceCeiling(pool.tokenA, pool.tokenB, stable, ratioX1000);
        }

        // Hermes must always claim rewards before withdrawing
        _harvestYieldByPool(poolId, true, true);

        // we don't sell the matched BIOS so we double the withdraw amount
        uint256 factor = pool.tokenA == biosAddress || pool.tokenB == biosAddress ? 2 : 1;

        // calculate amount of LP tokens
        (uint256 reserve0, uint256 reserve1) = getReserves(pool.tokenA, pool.tokenB, stable);
        uint256 liquidityToWithdraw = AMMLibrary.getLiquidityToWithdraw(
            token,
            amount * factor,
            stakings[poolId].balanceOf(address(this)),
            reserve0,
            reserve1,
            IERC20(pairFor(pool.tokenA, pool.tokenB, stable)).totalSupply(),
            pool
        );

        // unstake LP tokens from gauge
        stakings[poolId].withdraw(liquidityToWithdraw);

        // remove liquidity
        (uint256 amountTokenA, uint256 amountTokenB) = ISolidlyRouterV1(swapRouterAddress).removeLiquidity(
            pool.tokenA,
            pool.tokenB,
            stable,
            liquidityToWithdraw,
            0,
            0,
            address(this),
            block.timestamp
        );

        if (pool.tokenA == token) {
            if (factor == 2) {
                // handle BIOS
                amountWithdrawn = amountTokenA;
                balances[poolId][pool.tokenB] += amountTokenB;
            } else {
                amountWithdrawn =
                    amountTokenA +
                    _swapToken(
                        pool.tokenB,
                        pool.tokenA,
                        address(this),
                        amountTokenB,
                        _calculateSlip(
                            ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, stable)).current(pool.tokenB, amountTokenB)
                        ),
                        stable
                    )[1];
            }

            if (sendToKernel) {
                IERC20MetadataUpgradeable(pool.tokenA).safeTransfer(
                    moduleMap.getModuleAddress(Modules.Kernel),
                    amountWithdrawn
                );
            } else {
                balances[poolId][pool.tokenA] += amountWithdrawn;
            }
        } else {
            if (factor == 2) {
                // handle BIOS
                amountWithdrawn = amountTokenB;
                balances[poolId][pool.tokenA] += amountTokenA;
            } else {
                amountWithdrawn =
                    amountTokenB +
                    _swapToken(
                        pool.tokenA,
                        pool.tokenB,
                        address(this),
                        amountTokenA,
                        _calculateSlip(
                            ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, stable)).current(pool.tokenA, amountTokenA)
                        ),
                        stable
                    )[1];
            }

            if (sendToKernel) {
                IERC20MetadataUpgradeable(pool.tokenB).safeTransfer(
                    moduleMap.getModuleAddress(Modules.Kernel),
                    amountWithdrawn
                );
            } else {
                balances[poolId][pool.tokenB] += amountWithdrawn;
            }
        }

        emit WithdrawnFromPool(poolId, amountWithdrawn);
    }

    /// @dev Deploys all the tokens for the specified pools
    function deploy(uint32 poolId) external override onlyController {
        // we want to skip the LP deploy since we haven't rebalanced yet
        // otherwise we may get ILM error (https://github.com/MaiaDAO/hermes-protocol/blob/master/contracts/BaseV1-core.sol#L358)
        // _deploy(poolId);
    }

    function manualDeploy(uint32 poolId) external onlyManager {
        _deploy(poolId);
    }

    function _deploy(uint32 poolId) internal {
        Pool memory pool = pools[poolId];
        require(pool.tokenA != address(0), "pool doesn't exist");

        uint256 balanceA = balances[poolId][pool.tokenA];
        uint256 balanceB = balances[poolId][pool.tokenB];

        if (balanceA == 0 || balanceB == 0) {
            return;
        }
        bool stable = stablePools[poolId];
        (uint256 reserveA, uint256 reserveB) = getReserves(pool.tokenA, pool.tokenB, stable);

        (uint256 amountA, uint256 amountB) = AMMLibrary.getDeployAmounts(balanceA, balanceB, reserveA, reserveB);

        // Hermes must always claim fees before deploying more funds
        _harvestPoolFees(poolId, pool);
        _harvestGaugeRewards(poolId);

        (uint256 amountAActual, uint256 amountBActual, uint256 liquidityAcquired) = ISolidlyRouterV1(swapRouterAddress)
            .addLiquidity(pool.tokenA, pool.tokenB, stable, amountA, amountB, 0, 0, address(this), block.timestamp);
        balances[poolId][pool.tokenA] -= amountAActual;
        balances[poolId][pool.tokenB] -= amountBActual;
        emit LPTokensAcquired(liquidityAcquired);
        emit DepositToPool(poolId, liquidityAcquired);
    }

    function stakeLPTokens(uint32 poolId) external onlyManager {
        stakings[poolId].depositAll(0); // 0 unless we put our NFT in here?
    }

    /// @dev Harvests token yield from the integration
    function harvestYield() external override onlyController {
        uint256 yieldAmount;
        for (uint32 i; i <= poolCount; ++i) {
            uint256 poolYield = yieldBalances[i];
            if (poolYield > 0) {
                yieldAmount += poolYield;
                yieldBalances[i] = 0;
                harvestedYield[i] += yieldAmount;
            }
        }
        if (yieldAmount > 0) {
            IERC20MetadataUpgradeable(wnativeAddress).safeTransfer(
                moduleMap.getModuleAddress(Modules.YieldManager),
                yieldAmount
            );
            emit YieldReceived(yieldAmount);
        }
    }

    function _harvestYieldByPool(
        uint32 poolId,
        bool claimFees,
        bool claimRewards
    ) internal {
        Pool memory pool = pools[poolId];
        if (pool.tokenA != address(0)) {
            if (claimFees) {
                _harvestPoolFees(poolId, pool);
            }
            if (claimRewards) {
                _harvestGaugeRewards(poolId);
            }
        }
    }

    function _harvestPoolFees(uint32 poolId, Pool memory pool) internal {
        bool stable = stablePools[poolId];
        (uint256 token0Amount, uint256 token1Amount) = ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, stable))
            .claimFees();
        address biosAddress = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getBiosTokenAddress();
        // Swap for ETH, account to yieldBalances
        uint256 yieldAmount;
        uint256 biosAmount;
        if (pool.tokenA == wnativeAddress) {
            if (pool.tokenB == biosAddress) {
                // handle BIOS
                biosAmount = token1Amount;
                yieldAmount = token0Amount;
            } else {
                yieldAmount =
                    token0Amount +
                    _swapToken(
                        pool.tokenB,
                        pool.tokenA,
                        address(this),
                        token1Amount,
                        _calculateSlip(
                            ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, stable)).current(pool.tokenB, token1Amount)
                        ),
                        stable
                    )[1];
            }
        } else if (pool.tokenB == wnativeAddress) {
            if (pool.tokenA == biosAddress) {
                // handle BIOS
                biosAmount = token0Amount;
                yieldAmount = token1Amount;
            } else {
                yieldAmount =
                    token1Amount +
                    _swapToken(
                        pool.tokenA,
                        pool.tokenB,
                        address(this),
                        token0Amount,
                        _calculateSlip(
                            ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, stable)).current(pool.tokenA, token0Amount)
                        ),
                        stable
                    )[1];
            }
        } else {
            if (pool.tokenA == biosAddress) {
                biosAmount += token0Amount;
            } else {
                yieldAmount += _swapToken(
                    pool.tokenA,
                    wnativeAddress,
                    address(this),
                    token0Amount,
                    _calculateSlip(
                        ISolidlyPairV1(pairFor(pool.tokenA, pool.tokenB, false)).current(pool.tokenA, token0Amount)
                    ),
                    false
                )[1];
            }
            if (pool.tokenB == biosAddress) {
                biosAmount += token1Amount;
            } else {
                yieldAmount += _swapToken(
                    pool.tokenB,
                    wnativeAddress,
                    address(this),
                    token1Amount,
                    _calculateSlip(
                        ISolidlyPairV1(pairFor(pool.tokenB, wnativeAddress, false)).current(pool.tokenB, token1Amount)
                    ),
                    false
                )[1];
            }
        }

        // update balances
        if (yieldAmount > 0) {
            yieldBalances[poolId] += yieldAmount;
        }
        if (biosAmount > 0) {
            balances[poolId][biosAddress] += biosAmount;
        }
    }

    function _harvestGaugeRewards(uint32 poolId) internal {
        // get the reward and count how much
        address[] memory rewards = new address[](1);
        rewards[0] = rewardToken;
        IERC20 erc20 = IERC20(rewardToken);
        uint256 amountBefore = erc20.balanceOf(address(this));
        stakings[poolId].getReward(address(this), rewards);
        uint256 rewardAmount = IERC20(rewardToken).balanceOf(address(this)) - amountBefore;

        // liquidate the reward
        if (rewardAmount > 0) {
            yieldBalances[poolId] += _swapToken(
                rewardToken,
                wnativeAddress,
                address(this),
                rewardAmount,
                (ISolidlyPairV1(pairFor(rewardToken, wnativeAddress, false)).current(rewardToken, rewardAmount) * 99) /
                    100,
                false
            )[1];
        }
    }

    function harvestYieldByPool(uint32 poolId) external onlyManager {
        _harvestYieldByPool(poolId, true, true);
    }

    function harvestYieldByPools(uint32[] calldata poolIds) external onlyManager {
        uint256 length = poolIds.length;
        for (uint256 i; i < length; ++i) {
            _harvestYieldByPool(poolIds[i], true, true);
        }
    }

    function donate(uint32 poolId, uint256 amount) external {
        yieldBalances[poolId] += amount;

        IERC20MetadataUpgradeable metis = IERC20MetadataUpgradeable(
            IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress()
        );
        uint256 before = metis.balanceOf(address(this));
        metis.transferFrom(msg.sender, address(this), amount);
        uint256 received = metis.balanceOf(address(this)) - before;
        require(received == amount, "bad amount received");
    }

    /// @notice Rebalances by attempting to swap up to max to get into position ratio
    /// @param poolId The ID of the pool to rebalance
    /// @param ratioX1000 reserves ratio to control the price slippage
    /// @param maxSellTokenA max amount of tokenA to sell (to limit rebalancing)
    /// @param maxSellTokenB max amount of tokenB to sell (to limit rebalancing)
    function rebalancePool(
        uint32 poolId,
        uint256 ratioX1000,
        uint256 maxSellTokenA,
        uint256 maxSellTokenB
    ) external onlyManager {
        Pool memory pool = pools[poolId];
        bool stable = stablePools[poolId];

        require(pools[poolId].tokenA != address(0), "Pool doesn't exist");

        checkPriceCeiling(pool.tokenA, pool.tokenB, stable, ratioX1000);

        // determine which token and how much to swap
        (address swapToken, uint256 excessAmountToSwap) = _calculateExcessTokensToSwap(
            pool,
            balances[poolId][pool.tokenA],
            balances[poolId][pool.tokenB],
            stable
        );

        // handle tokenA vs tokenB, cap at max sell param

        (uint256 swapAmount, address targetToken) = AMMLibrary.getRebalanceAmounts(
            swapToken,
            pool,
            excessAmountToSwap,
            maxSellTokenA,
            maxSellTokenB
        );

        // swap tokens
        uint256 amountReceived = _swapToken(
            swapToken,
            targetToken,
            address(this),
            swapAmount,
            _calculateSlip((ISolidlyPairV1(pairFor(swapToken, targetToken, stable)).current(swapToken, swapAmount))),
            stable
        )[1];

        // update pool token balances
        if (amountReceived > 0) {
            balances[poolId][swapToken] -= swapAmount;
            balances[poolId][targetToken] += amountReceived;

            emit PoolRebalanced(poolId, swapToken, swapAmount, targetToken, amountReceived);
        }
    }

    function createPool(
        address tokenA,
        address tokenB,
        bool stable
    ) external onlyManager {
        poolCount++;
        uint32 id = poolCount;
        (address token0, address token1) = AMMLibrary.sortTokens(tokenA, tokenB);
        stablePools[id] = stable;
        pools[id] = Pool({tokenA: token0, tokenB: token1, positionID: 0});
        _setMaxAllowance(token0, swapRouterAddress);
        _setMaxAllowance(token1, swapRouterAddress);
        _setMaxAllowance(pairFor(token0, token1, stable), swapRouterAddress);
    }

    function configureAllowance() external onlyManager {
        _setMaxAllowance(rewardToken, swapRouterAddress);
        for (uint32 i = 1; i <= poolCount; ++i) {
            Pool memory pool = pools[i];
            _setMaxAllowance(pool.tokenA, swapRouterAddress);
            _setMaxAllowance(pool.tokenB, swapRouterAddress);
            _setMaxAllowance(pairFor(pool.tokenA, pool.tokenB, stablePools[i]), swapRouterAddress);
        }
    }

    function _setMaxAllowance(address token, address spender) internal {
        IERC20MetadataUpgradeable ERC20 = IERC20MetadataUpgradeable(token);
        if (ERC20.allowance(address(this), spender) == 0) {
            ERC20.safeApprove(spender, type(uint256).max);
        }
    }

    function setSlippage(uint24 slippageNumerator_) external onlyManager {
        slippageNumerator = slippageNumerator_;
    }

    function configureStaking(uint32 poolId, address gaugeAddress) external onlyManager {
        _configureStaking(poolId, gaugeAddress, false);
    }

    function configureStakingOverwrite(uint32 poolId, address gaugeAddress) external onlyManager {
        _configureStaking(poolId, gaugeAddress, true);
    }

    function _configureStaking(
        uint32 poolId,
        address gaugeAddress,
        bool overwrite
    ) internal {
        Pool memory pool = pools[poolId];
        require(pool.tokenA != address(0), "Pool doesn't exist");

        if (overwrite != true) {
            require(address(stakings[poolId]) == address(0), "Staking already configured");
        }

        address pairAddress = pairFor(pool.tokenA, pool.tokenB, stablePools[poolId]);

        ISolidlyGauge gauge = ISolidlyGauge(gaugeAddress);
        require(gauge.stake() == pairAddress, "Incorrect gauge address");

        stakings[poolId] = gauge;

        // let gauge pull tokens
        IERC20MetadataUpgradeable pairToken = IERC20MetadataUpgradeable(pairAddress);
        if (pairToken.allowance(address(this), gaugeAddress) <= type(uint256).max) {
            pairToken.safeApprove(gaugeAddress, 0);
            pairToken.safeApprove(gaugeAddress, type(uint256).max);
        }
    }

    function _calculateExcessTokensToSwap(
        Pool memory pool,
        uint256 balanceA,
        uint256 balanceB,
        bool stable
    ) internal view returns (address swapToken, uint256 excessAmountToSwap) {
        require(balanceA + balanceB > 0, "hermes: no balance");
        // fetch reserve values from hermes
        (uint256 reserveA, uint256 reserveB) = getReserves(pool.tokenA, pool.tokenB, stable);

        (swapToken, excessAmountToSwap) = AMMLibrary.getExcessTokenAmounts(
            pool,
            balanceA,
            balanceB,
            reserveA,
            reserveB
        );
    }

    function getReserves(
        address tokenA,
        address tokenB,
        bool stable
    ) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = AMMLibrary.sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = ISolidlyPairV1(pairFor(tokenA, tokenB, stable)).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    /// @param tokenA The address of tokenA
    /// @param tokenB The address of tokenB
    /// @return pair The address of the pool contract
    function pairFor(
        address tokenA,
        address tokenB,
        bool stable
    ) internal view returns (address pair) {
        pair = ISolidlyFactoryV1(factoryAddress).getPair(tokenA, tokenB, stable);
    }

    function checkPriceCeiling(
        address tokenA,
        address tokenB,
        bool stable,
        uint256 ratioX1000
    ) internal view {
        address pairAddress = address(pairFor(tokenA, tokenB, stable));
        (uint256 reserveA, uint256 reserveB) = getReserves(tokenA, tokenB, stable);

        AMMLibrary.checkPriceCeiling(pairAddress, reserveA, reserveB, ratioX1000);
    }

