    /// @param tokenIn The address of the input token
    /// @param tokenOut The address of the output token
    /// @param recipient The address of the token out recipient
    /// @param amountIn The exact amount of the input to swap
    function _swapExactInput(
        address tokenIn,
        address tokenOut,
        address recipient,
        uint256 amountIn,
        uint256 amountOutMin,
        bool stable
    ) internal returns (uint256[] memory amounts) {
        ISolidlyRouterV1.route[] memory path = new ISolidlyRouterV1.route[](1);
        path[0] = ISolidlyRouterV1.route({from: tokenIn, to: tokenOut, stable: stable});
        uint256 deadline = block.timestamp;
        if (IERC20MetadataUpgradeable(tokenIn).allowance(address(this), swapRouterAddress) < amountIn) {
            IERC20MetadataUpgradeable(tokenIn).safeApprove(swapRouterAddress, 0);
            IERC20MetadataUpgradeable(tokenIn).safeApprove(swapRouterAddress, amountIn);
        }
        return
            ISolidlyRouterV1(swapRouterAddress).swapExactTokensForTokens(
                amountIn,
                amountOutMin,
                path,
                recipient,
                deadline
            );
    }

