pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";
import "../interfaces/IIntegration.sol";
import "../interfaces/IOTCIntegrationV0.sol";

contract OTCIntegrationV0 is Initializable, ModuleMapConsumer, Controlled, IOTCIntegrationV0 {
    using SafeERC20 for IERC20Metadata;

    // token => balance
    mapping(address => uint256) private _balances;

    function initialize(address[] memory _controllers, address _moduleMap) external override initializer {
        __Controlled_init(_controllers, _moduleMap);
    }

    /**
     * Deposit ETH to the OTC contract
     * @dev Send ETH value with this call
     */
    receive() external payable override {
        if (msg.value != 0) {
            emit Deposit(address(0), msg.value);
        }
    }

    /**
     * Withdraw ETH from the OTC contract
     * @param amount Amount to withdraw
     * @param to Destination of withdrawn ETH
     */
    function withdrawEth(uint256 amount, address payable to) external override onlyManager {
        if (amount != 0) {
            require(address(to) != address(0), "bad address");
            _transferEth(amount, to);
            emit Withdraw(address(0), amount);
        }
    }

    /**
     * Send ETH from the OTC contract to a recipient.
     * @param amount Amount to send
     * @param to Recipient to send ETH to
     */
    function sendEth(uint256 amount, address payable to) external override onlyManager {
        if (amount != 0) {
            require(address(to) != address(0), "bad address");
            _transferEth(amount, to);
            emit Send(to, address(0), amount);
        }
    }

    /**
     * Used by YieldManager to deposit funds.
     * @param token The address of the deposited token
     * @param amount The amount being deposited
     */
    function deposit(address token, uint256 amount) external override onlyController {
        uint256 balance = _selfBalance(token);
        require(balance - _balances[token] >= amount, "bad amount");
        _balances[token] = balance;
        emit Deposit(token, amount);
    }

    /**
     * Used by YieldManager to withdraw funds.
     * @param token The address of the withdrawal token
     * @param amount The amount being withdrawn
     */
    function withdraw(address token, uint256 amount) external override onlyController {
        _balances[token] -= amount;
        _transferTo(token, moduleMap.getModuleAddress(Modules.Kernel), amount);
        emit Withdraw(token, amount);
    }

    /**
     * Send funds from the OTC contract to a recipient.
     * @param to Address to send the tokens to
     * @param tokens Tokens to send
     * @param amounts Amounts to send
     */
    function send(
        address to,
        address[] calldata tokens,
        uint256[] calldata amounts
    ) external override onlyManager {
        require(to != address(0), "bad address");
        uint256 length = tokens.length;
        require(length == amounts.length, "bad lengths");

        for (uint256 i; i < length; ++i) {
            if (amounts[i] != 0) {
                _balances[tokens[i]] -= amounts[i];
                _transferTo(tokens[i], to, amounts[i]);
                emit Send(to, tokens[i], amounts[i]);
            }
        }
    }

    /**
     * Deposit funds from a depositor into the OTC contract.
     * @param from Address to deposit the tokens from
     * @param tokens Tokens to deposit
     * @param amounts Amounts to deposit
     */
    function depositFrom(
        address from,
        address[] calldata tokens,
        uint256[] calldata amounts
    ) external override onlyManager {
        uint256 length = tokens.length;
        require(length == amounts.length, "bad lengths");

        for (uint256 i; i < length; ++i) {
            if (amounts[i] != 0) {
                _transferFrom(tokens[i], from, amounts[i]);
                uint256 balance = _selfBalance(tokens[i]);
                require(balance - _balances[tokens[i]] >= amounts[i], "not enough");
                _balances[tokens[i]] = balance;
                emit DepositFrom(from, tokens[i], amounts[i]);
            }
        }
    }

    /**
     * Admin method to update accounting
     * @param token The token to update
     * @param amount The amount to adjust the balance by
     * @param add True for add, false for subtract
     */
    function updateBalance(
        address token,
        uint256 amount,
        bool add
    ) external override onlyManager {
        if (add) {
            _balances[token] += amount;
        } else {
            _balances[token] -= amount;
        }
        emit BalanceUpdated(token, amount, add);
    }

    /**
     * This is just here to satisfy the interface and allow YieldManager to
     * call during system deploy
     */
    function deploy() external override onlyController {}

    /**
     * This is just here to satisfy the interface
     */
    function harvestYield() external override onlyController {}

    /**
     * Get the token balance.
     * @param token The address of the token
     * @return The balance of the token
     */
    function getBalance(address token) external view override returns (uint256) {
        return token == address(0) ? address(this).balance : _balances[token];
    }

    /**
     * This is just here to satisfy the interface
     */
    function getPendingYield(address) external pure override returns (uint256) {
        return 0;
    }

    /**
     * Send Ether from this contract to a destination
     * @param amount Amount of Ether to send
     * @param to Address to send the Ether to
     */
    function _transferEth(uint256 amount, address to) internal {
        (bool sent, ) = payable(to).call{value: amount}("");
        require(sent, "Eth transfer fail");
    }

    /**
     * Send an amount of ERC20 token FROM a sender TO this contract
     * This contract must be permitted to spend the sender's tokens
     * @param token The ERC20 token to transfer
     * @param from The address of the token sender
     * @param amount The amount of the token to transfer in
     */
    function _transferFrom(
        address token,
        address from,
        uint256 amount
    ) internal {
        IERC20Metadata(token).safeTransferFrom(from, address(this), amount);
    }

    /**
     * Send an amount of ERC20 token FROM this contract TO a recipient
     * @param token The ERC20 token to transfer
     * @param to The address of the recipient to send tokens to
     * @param amount The amount of the token to transfer out
     */
    function _transferTo(
        address token,
        address to,
        uint256 amount
    ) internal {
        IERC20Metadata(token).safeTransfer(to, amount);
    }

    /**
     * Check the contract's balance of an ERC20 token
     * @param token The token to check the balance of
     * @return The contract's current balance of the specified token
     */
    function _selfBalance(address token) internal view returns (uint256) {
        return IERC20Metadata(token).balanceOf(address(this));
    }
}
