/**
 * @title Proxy clone implementation
 * @dev
 * Like EIP-1167, but for proxies,
 * meaning that clone will delegate to the proxy which will delegate to the actual implemenation.
 * Address of the proxy is immutable, but implementation can be changed via API supporting EIP-1967
 **/
contract ProxyClone {
    // bytes32(uint256(keccak256('eip1967.proxy.admin')) - 1)
    bytes32 constant PROXY_ADMIN_HASH = 0xb53127684a568b3173ae13b9f8a6016e243e63b6e8ee1178d6a717850b5d6103;

    // bytes32(uint256(keccak256('eip1967.proxy.implementation')) - 1)
    bytes32 constant IMPLEMENTATION_HASH = 0x360894a13ba1a3210667c828492db98dca3e2076cc3735a920a3ca505d382bbc;

    // bytes32(uint256(keccak256('clone.proxy')) - 1)
    bytes32 constant PROXY_HASH = 0x12fe31f5c6f7b7eb37c3a9b1bcb2048b5844d6998ddcca840cc9fa541abf2a59;

    constructor(
        address _proxyAdmin,
        address _proxy,
        address _implementation
    ) {
        // set initial proxy parameters using EIP-1967
        assembly {
            sstore(PROXY_ADMIN_HASH, _proxyAdmin)
            sstore(PROXY_HASH, _proxy)
            sstore(IMPLEMENTATION_HASH, _implementation)
        }
    }

    fallback() external {
        assembly {
            // we delegate to the proxy, which handle upgrade logic and delegating to the actual implementation
            let proxy := sload(PROXY_HASH)

            // Copy msg.data. We take full control of memory in this inline assembly
            // block because it will not return to Solidity code. We overwrite the
            // Solidity scratch pad at memory position 0.
            calldatacopy(0, 0, calldatasize())

            // Call the proxy.
            // out and outsize are 0 because we don't know the size yet.
            let result := delegatecall(gas(), proxy, 0, calldatasize(), 0, 0)

            // Copy the returned data.
            returndatacopy(0, 0, returndatasize())

            switch result
            // delegatecall returns 0 on error.
            case 0 {
                revert(0, returndatasize())
            }
            default {
                return(0, returndatasize())
            }
        }
    }
}
