pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";

import "../interfaces/IProxyCloneFactory.sol";
import "../interfaces/IGenericInitializable.sol";
import "../interfaces/IStrategyMap.sol";

import "./ProxyClone.sol";

import "../core/ModuleMapConsumer.sol";
import "../core/Controlled.sol";

/**
 * @title Proxy clones factory
 * @dev
 * Like EIP-1167, but for upgradeable contracts,
 * meaning that clone will delegate to proxy which will delegate to actual implemenation.
 * Address of the proxy is immutable, but implementation can be changed via API supporting EIP-1967
 **/
contract ProxyCloneFactory is Initializable, ModuleMapConsumer, Controlled, IProxyCloneFactory {
    // keccak256(implementation contract name) => { proxy; proxyAdmin; implementation; salt }
    mapping(bytes32 => Implementation) public implementations;

    function initialize(address[] calldata controllers_, address moduleMap_) public initializer {
        __Controlled_init(controllers_, moduleMap_);
    }

    function getNextCloneAddress(bytes32 _implementationHash) external view override returns (address) {
        Implementation storage implementation = implementations[_implementationHash];

        bytes memory bytecode = getBytecode(
            implementation.proxyAdmin,
            implementation.proxy,
            implementation.implementation
        );
        bytes32 salt = getSalt(_implementationHash, implementation.salt + 1);

        bytes32 hash = keccak256(abi.encodePacked(bytes1(0xFF), address(this), salt, keccak256(bytecode)));

        return address(uint160(uint256(hash)));
    }

    function deployClone(bytes32 _implementationHash, bytes calldata _initData)
        external
        override
        onlyController
        returns (address deployedCloneAddress_)
    {
        Implementation storage implementation = implementations[_implementationHash];
        implementation.salt++;

        bytes memory bytecode = getBytecode(
            implementation.proxyAdmin,
            implementation.proxy,
            implementation.implementation
        );
        bytes32 salt = getSalt(_implementationHash, implementation.salt);

        assembly {
            deployedCloneAddress_ := create2(
                callvalue(),
                add(bytecode, 0x20), // first 32 bytes occupied by the length
                mload(bytecode), // bytecode length
                salt
            )
        }
        require(deployedCloneAddress_.code.length > 0, "proxy clone creation failed");

        IGenericInitializable(deployedCloneAddress_).initialize(_initData);

        emit CloneDeployed(_implementationHash, deployedCloneAddress_);
    }

    function setImplementation(
        bytes32 _implementationHash,
        address _proxyAdminAddress,
        address _proxyAddress,
        address _implementationAddress
    ) external override onlyManager {
        implementations[_implementationHash].proxyAdmin = _proxyAdminAddress;
        implementations[_implementationHash].proxy = _proxyAddress;
        implementations[_implementationHash].implementation = _implementationAddress;

        emit SetImplementation(_implementationHash, _proxyAdminAddress, _proxyAddress, _implementationAddress);
    }

    function getBytecode(
        address _proxyAdmin,
        address _proxy,
        address _implementation
    ) internal pure returns (bytes memory) {
        bytes memory bytecode = type(ProxyClone).creationCode;
        return abi.encodePacked(bytecode, abi.encode(_proxyAdmin, _proxy, _implementation));
    }

    function getSalt(bytes32 _implementationHash, uint256 _rawSalt) internal pure returns (bytes32) {
        bytes32 typeUniqueness = keccak256(abi.encodePacked(_implementationHash));
        uint256 uintSalt = uint256(typeUniqueness) + _rawSalt;
        return keccak256(abi.encodePacked(uintSalt));
    }
}
