pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";

import "../interfaces/ISmartRewardDistributor.sol";

contract SmartRewardDistributor is Initializable, ModuleMapConsumer, Controlled, ISmartRewardDistributor {
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    struct DistributionRatio {
        uint128 rawTransferTarget;
        uint128 customDataTarget;
    }

    DistributionRatio ratio;

    address rawTransferTarget;

    // customData call target
    address customDataTarget;
    // abi.encodeWithSignature("functionname(immutableArgs,uint256)", immutableArgs)
    // where last uint256 represents distributionAmount, and will be added before call
    bytes customData;

    function initialize(
        address[] memory controllers_,
        address moduleMap_,
        DistributionRatio calldata _ratio,
        address _rawTransferTarget,
        address _customDataTarget,
        bytes calldata _customData
    ) public initializer {
        __Controlled_init(controllers_, moduleMap_);
        ratio = _ratio;
        rawTransferTarget = _rawTransferTarget;
        customDataTarget = _customDataTarget;
        customData = _customData;
    }

    function setDistributionParameters(
        DistributionRatio calldata _ratio,
        address _rawTransferTarget,
        address _customDataTarget,
        bytes calldata _customData
    ) external onlyOwner {
        ratio = _ratio;
        rawTransferTarget = _rawTransferTarget;
        customDataTarget = _customDataTarget;
        customData = _customData;
    }

    function distributeRewards(address _token, uint256 _amount) external override {
        DistributionRatio memory mratio = ratio;
        require(mratio.rawTransferTarget + mratio.customDataTarget > 0, "distribution ratio is not set");

        uint256 rawTransferAmount = (_amount * mratio.rawTransferTarget) /
            (mratio.rawTransferTarget + mratio.customDataTarget);
        uint256 customDataAmount = _amount - rawTransferAmount;

        if (rawTransferAmount > 0) {
            IERC20MetadataUpgradeable(_token).safeTransferFrom(msg.sender, rawTransferTarget, rawTransferAmount);
        }
        if (customDataAmount > 0) {
            IERC20MetadataUpgradeable(_token).safeTransferFrom(msg.sender, customDataTarget, customDataAmount);

            (bool success, ) = customDataTarget.call(abi.encodePacked(customData, customDataAmount));

            require(success, "distribution low-level call fail");
        }
    }
}
