pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../interfaces/IStrategyERC20.sol";
import "../interfaces/IClone.sol";
import "../interfaces/IEtherRewards.sol";

import "../core/ModuleMapConsumer.sol";
import "../core/Controlled.sol";

contract StrategyERC20 is Initializable, ModuleMapConsumer, Controlled, ERC20Upgradeable, IStrategyERC20, IClone {
    uint256 id;

    function initialize(bytes calldata _data) external override initializer {
        (
            string memory _name,
            string memory _symbol,
            uint256 _id,
            address[] memory controllers_,
            address moduleMap_
        ) = abi.decode(_data, (string, string, uint256, address[], address));

        __ERC20_init(_name, _symbol);
        __Controlled_init(controllers_, moduleMap_);
        id = _id;
    }

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        if (from != address(0)) {
            IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).decreaseUserRewards(id, amount, from);
        }

        if (to != address(0)) {
            IEtherRewards(moduleMap.getModuleAddress(Modules.EtherRewards)).increaseUserRewards(id, amount, to);
        }
    }

    function mint(address _account, uint256 _amount) public override onlyController {
        _mint(_account, _amount);
    }

    function burn(address _account, uint256 _amount) public override onlyController {
        _burn(_account, _amount);
    }

    function totalSupply() public view override(ERC20Upgradeable, IStrategyERC20) returns (uint256) {
        return ERC20Upgradeable.totalSupply();
    }

    function balanceOf(address account) public view override(ERC20Upgradeable, IStrategyERC20) returns (uint256) {
        return ERC20Upgradeable.balanceOf(account);
    }
}
