","keccak256":"0x562431546443640c950a15ccce0cffedeced3e414c41cc2758bf489f4525d1df","license":"GPL-2.0"},"contracts/core/SwapManager.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity ^0.8.4;

import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "../interfaces/ISwapManager.sol";
import "../interfaces/IDexTrader.sol";
import "../interfaces/ISyntheticERC20.sol";
import "../interfaces/IIntegrationMap.sol";

import "../libraries/SqrtMath.sol";

import "./Controlled.sol";
import "./ModuleMapConsumer.sol";

contract SwapManager is Initializable, ModuleMapConsumer, Controlled, ISwapManager {
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    // Addresses of dex traders.
    // For easy adding new dexes, this contract use traders addresses
    // instead of enum values in moduleMap.
    EnumerableSetUpgradeable.AddressSet traders;
    // Address of the contract or EOA which will be used for internal swaps
    address public internalWallet;

    function initialize(
        address[] memory _controllers,
        address _moduleMap,
        address _internalWallet
    ) external initializer {
        __Controlled_init(_controllers, _moduleMap);
        internalWallet = _internalWallet;
    }

    /**
     *
     *
     * swaps
     *
     *
     **/

    /// @dev Executes swap only for amountIn which will not significantly imbalance the pool and returns this amountIn
    function gentleSwapExactIn(
        address _tokenIn,
        address _tokenOut,
        uint32 _ratioInLimit,
        uint32 _ratioOutLimit,
        address _recipient,
        uint256 _desiredAmountIn,
        uint256 _desiredMinAmountOut
    ) external override onlyController returns (uint256) {
        IDexTrader trader;
        {
            address traderAddress;

            (traderAddress, ) = getBestTraderForTheSwap(_tokenIn, _tokenOut, _desiredAmountIn);
            if (traderAddress == address(0)) return zeroSwap(_tokenIn);

            trader = IDexTrader(traderAddress);
        } // stack too deep
        (uint256 inReserve, uint256 outReserve) = trader.getReserves(_tokenIn, _tokenOut);

        uint256 gentleAmountIn = constraintInputAmountToNotExhaustThePool(
            _desiredAmountIn,
            inReserve,
            outReserve,
            IERC20MetadataUpgradeable(_tokenIn).decimals(),
            IERC20MetadataUpgradeable(_tokenOut).decimals(),
            _ratioInLimit,
            _ratioOutLimit
        );

        if (gentleAmountIn == 0) return zeroSwap(_tokenIn); // we have reached the limit to draw from the pool

        if (gentleAmountIn < _desiredAmountIn) {
            _desiredMinAmountOut = (_desiredMinAmountOut * gentleAmountIn) / _desiredAmountIn;
        }
        uint256 minAmountOutFromRatio = getMinAmountOutFromRatio(_ratioInLimit, _ratioOutLimit, gentleAmountIn);
        if (minAmountOutFromRatio > _desiredMinAmountOut) _desiredMinAmountOut = minAmountOutFromRatio;

        transferTokenToDex(_tokenIn, address(trader), gentleAmountIn);

        require(
            trader.swapExactInput(_tokenIn, _tokenOut, _recipient, gentleAmountIn, _desiredMinAmountOut),
            "swap failed"
        );
        return gentleAmountIn;
    }

    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn
    ) external override onlyController returns (bool tradeSuccess_) {
        return swapExactIn(_tokenIn, _tokenOut, _recipient, _amountIn, 0);
    }

    function swapExactIn(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _minAmountOut
    ) public override onlyController returns (bool tradeSuccess_) {
        (address bestTrader, uint256 minOut) = getBestTraderForTheSwap(_tokenIn, _tokenOut, _amountIn);

        uint256 trueMinAmountOut = minOut >= _minAmountOut ? minOut : _minAmountOut;

        if (trueMinAmountOut > 0) {
            tradeSuccess_ = swapUsingInternalWallet(_tokenIn, _tokenOut, _recipient, trueMinAmountOut);
            if (tradeSuccess_) {
                return tradeSuccess_;
            }
        }

        IDexTrader trader = IDexTrader(bestTrader);
        transferTokenToDex(_tokenIn, bestTrader, _amountIn);
        tradeSuccess_ = trader.swapExactInput(_tokenIn, _tokenOut, _recipient, _amountIn, trueMinAmountOut);

        require(tradeSuccess_, "swap failed");
    }

    function biosBuyBack(uint256 ratioX1000) external override onlyController returns (bool) {
        IIntegrationMap integrationMap = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap));
        address wethAddress = integrationMap.getWethTokenAddress();
        address biosAddress = integrationMap.getBiosTokenAddress();

        uint256 wethAmountIn = IERC20MetadataUpgradeable(wethAddress).balanceOf(address(this));

        (address bestTrader, uint256 biosAmountOutMin) = getBestTraderForTheSwap(
            wethAddress,
            biosAddress,
            wethAmountIn
        );

        // Front runner protection
        checkPriceCeiling(bestTrader, wethAddress, biosAddress, ratioX1000);

        transferTokenToDex(wethAddress, bestTrader, wethAmountIn);

        return
            IDexTrader(bestTrader).swapExactInput(
                wethAddress,
                biosAddress,
                moduleMap.getModuleAddress(Modules.Kernel),
                wethAmountIn,
                biosAmountOutMin
            );
    }

    /**
     *
     *
     * manager
     *
     *
     **/

    /// @dev Adds trader which will be used for futher swaps
    function addTrader(address _traderAddress) external onlyManager {
        require(!traders.contains(_traderAddress), "SwapManager::addTrader: Trader already added");
        traders.add(_traderAddress);
    }

    /// @dev Removes trader so we will be not able to use it for swaps
    function removeTrader(address _traderAddress) external onlyManager {
        require(traders.contains(_traderAddress), "SwapManager::removeTrader: Trader was not added");
        traders.remove(_traderAddress);
    }

    /// @dev If we have sufficient amount of money we will use that money instead of swap via traders
    /// @param _traderAddress Address of the contract implementing IDexTrader interface
    function hasTrader(address _traderAddress) external view onlyManager returns (bool) {
        return traders.contains(_traderAddress);
    }

    /// @param _walletAddress The address of the contract (user) which will be used for internal swaps. Set to Zero address to disable internal
    function setInternalWallet(address _walletAddress) external onlyManager {
        internalWallet = _walletAddress;
    }

    /**
     *
     *
     * getters
     *
     *
     **/

    /// @return isPoolExist_ Can we swap these tokens via added traders?
    /// @return bestPrice_ Best amount out from all traders
    function getBestAmountOut(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external view override returns (bool isPoolExist_, uint256 bestPrice_) {
        for (uint8 i = 0; i < traders.length(); i++) {
            IDexTrader trader = IDexTrader(traders.at(i));
            uint256 amountOut;

            if (trader.isPoolExist(_tokenIn, _tokenOut)) {
                amountOut = trader.getAmountOut(_tokenIn, _tokenOut, _amountIn);
                if (!isPoolExist_) isPoolExist_ = true;
            }

            if (amountOut > bestPrice_) {
                bestPrice_ = amountOut;
            }
        }
    }

    /// @dev iterates over all added traders and finds best for swapping
    /// @dev uses getAmountOut() for comparing
    function getBestTraderForTheSwap(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) internal view returns (address bestTrader_, uint256 minAmountOut_) {
        uint256 maxAmountOut;
        for (uint8 i; i < traders.length(); ++i) {
            IDexTrader trader = IDexTrader(traders.at(i));
            uint256 amountOut;
            uint256 amountOutMin;

            if (trader.isPoolExist(_tokenIn, _tokenOut)) {
                amountOut = trader.getAmountOut(_tokenIn, _tokenOut, _amountIn);
                amountOutMin = trader.getAmountOutMinimum(_tokenIn, _tokenOut, _amountIn);
            }

            if (amountOut > maxAmountOut) {
                bestTrader_ = traders.at(i);
                maxAmountOut = amountOut;
                minAmountOut_ = amountOutMin;
            }
        }
    }

    /**
     *
     *
     * help methods
     *
     *
     **/

    /// @dev tries to swap tokens using internal wallet instead of external dexes
    function swapUsingInternalWallet(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _desiredAmount
    ) private returns (bool tradeSuccess_) {
        if (internalWallet == address(0)) {
            return false;
        }

        IERC20MetadataUpgradeable tokenIn = IERC20MetadataUpgradeable(_tokenIn);
        IERC20MetadataUpgradeable tokenOut = IERC20MetadataUpgradeable(_tokenOut);

        uint256 walletTokenOutBalance = tokenOut.balanceOf(internalWallet);
        uint256 walletAllowanceToThis = tokenOut.allowance(internalWallet, address(this));

        if (walletTokenOutBalance >= _desiredAmount && walletAllowanceToThis >= _desiredAmount && _desiredAmount > 0) {
            if (internalWallet != _recipient) {
                tokenOut.safeTransferFrom(internalWallet, _recipient, _desiredAmount);
            }

            uint256 tokenInBalance = tokenIn.balanceOf(address(this));
            if (tokenInBalance > 0) tokenIn.safeTransfer(internalWallet, tokenInBalance);

            tradeSuccess_ = true;
        }
    }

    /// @dev Prevent token locking on the SwapManager contract by sending remained token balance to internalWallet
    /// @dev Should be returned instead of plain 'return 0;' when something went wrong and transferTokenToDex wasn't called
    function zeroSwap(address _token) private returns (uint256) {
        IERC20MetadataUpgradeable token = IERC20MetadataUpgradeable(_token);
        uint256 balance = token.balanceOf(address(this));

        if (balance > 0) token.safeTransfer(internalWallet, balance);

        return 0;
    }

    /// @dev Transfers _amount to dex and makes sure that token won't be not locked on this contract
    function transferTokenToDex(
        address _token,
        address _trader,
        uint256 _amount
    ) private {
        IERC20MetadataUpgradeable token = IERC20MetadataUpgradeable(_token);
        uint256 balance = token.balanceOf(address(this));

        if (balance == _amount) {
            token.safeTransfer(_trader, _amount);
        } else if (balance > _amount) {
            // protect from locking tokens on the contract
            token.safeTransfer(_trader, _amount);
            token.safeTransfer(internalWallet, balance - _amount);
        } else {
            uint256 missingAmount = _amount - balance;

            if (balance > 0) {
                token.safeTransfer(_trader, balance);
            }

            token.safeTransferFrom(internalWallet, _trader, missingAmount);
        }
    }

    function getMinAmountOutFromRatio(
        uint256 ratioIn,
        uint256 ratioOut,
        uint256 amountIn
    ) internal pure returns (uint256) {
        return (ratioOut * amountIn) / ratioIn;
    }

    function constraintInputAmountToNotExhaustThePool(
        uint256 _inputAmountA,
        uint256 _reserveA,
        uint256 _reserveB,
        uint256 _decimalA,
        uint256 _decimalB,
        uint32 _ratioALimit,
        uint32 _ratioBLimit
    ) public pure returns (uint256 constraintedA_) {
        uint256 resultDenominator = 1;
        if (_decimalA > _decimalB) {
            _reserveB *= 10**(_decimalA - _decimalB);
        } else if (_decimalA < _decimalB) {
            resultDenominator = 10**(_decimalB - _decimalA);
            _reserveA *= resultDenominator;
            _inputAmountA *= resultDenominator;
        }

        return
            _constraintInputAmountToNotExhaustThePool(_inputAmountA, _reserveA, _reserveB, _ratioALimit, _ratioBLimit) /
            resultDenominator;
    }

    function _constraintInputAmountToNotExhaustThePool(
        uint256 _inputAmountA,
        uint256 _reserveA,
        uint256 _reserveB,
        uint32 _ratioALimit,
        uint32 _ratioBLimit
    ) private pure returns (uint256 constraintedA_) {
        uint256 outputAmountB = (_inputAmountA * _reserveB) / (_reserveA + _inputAmountA);

        // First, we check whether the _inputAmount exceeds the limit value in the pool
        if (
            // and if it not we can allow to use the _inputAmount as an input
            (_reserveA + _inputAmountA) * _ratioBLimit <= (_reserveB - outputAmountB) * _ratioALimit
        ) {
            constraintedA_ = _inputAmountA;
            // But if _inputAmount will cause exceeding the limit ratio in the pool we have to constraint it
        } else {
            // This part is quite expensive due to SqrtMath.sqrt(..) operation and because of that
            // we will use it only if we have and _inputAmountA is unacceptable
            uint256 maxAInPoolSqrt = (_ratioALimit * _reserveA * _reserveB) / _ratioBLimit;
            uint256 reserveLimitA = SqrtMath.sqrt(maxAInPoolSqrt);

            constraintedA_ = reserveLimitA > _reserveA ? reserveLimitA - _reserveA : 0;
            constraintedA_ = _inputAmountA > constraintedA_ ? constraintedA_ : _inputAmountA;
        }
    }

    function checkPriceCeiling(
        address trader,
        address tokenA,
        address tokenB,
        uint256 ratioX1000
    ) internal view {
        uint256 currentRatio;
        (uint256 reserveA, uint256 reserveB) = IDexTrader(trader).getReserves(tokenA, tokenB);

        if (reserveA < reserveB) {
            currentRatio = (reserveB * 1000) / reserveA;
        } else if (reserveB < reserveA) {
            currentRatio = (reserveA * 1000) / reserveB;
        } else {
            currentRatio = 1000;
        }

        // limit to 1% off
        if (currentRatio > ratioX1000) {
            require(((currentRatio - ratioX1000) * 100) / ratioX1000 < 1, "CeilingLimitReached");
        } else if (ratioX1000 > currentRatio) {
            require(((ratioX1000 - currentRatio) * 100) / ratioX1000 < 1, "CeilingLimitReached");
        }
    }
}
","keccak256":"0x46cfe0e32f67805bc2fe9821d5358d7e676ef8be63dc15fd095182b9f0ab631a","license":"GPL-2.0"},"contracts/interfaces/IDexTrader.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

interface IDexTrader {
    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _recipient The address of the token out recipient
    /// @param _amountIn The exact amount of the input to swap
    /// @param _amountOutMin The minimum amount of tokenOut to receive from the swap
    /// @return bool Indicates whether the swap succeeded
    function swapExactInput(
        address _tokenIn,
        address _tokenOut,
        address _recipient,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) external returns (bool);

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _amountIn The exact amount of the input to swap
    /// @return uint256 The estimated amount of tokenOut to receive
    function getAmountOut(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external view returns (uint256);

    /// @param _tokenIn The address of the input token
    /// @param _tokenOut The address of the output token
    /// @param _amountIn The exact amount of the input to swap
    /// @return uint256 The minimum amount of tokenOut to receive, factoring in allowable slippage
    function getAmountOutMinimum(
        address _tokenIn,
        address _tokenOut,
        uint256 _amountIn
    ) external view returns (uint256);

    /// @param _tokenA The address of the first  token from the token pair
    /// @param _tokenB The address of the second token from the token pair
    /// return true if pool exist else overwise
    function isPoolExist(address _tokenA, address _tokenB) external view returns (bool);

    function getReserves(address _tokenA, address _tokenB) external view returns (uint256 _amountA, uint256 _amountB);
}
","keccak256":"0x9f027816998fa915308813c472b7c5b7bb5db5c81d54df8a75546e2527497803","license":"GPL-2.0"},"contracts/interfaces/IIntegrationMap.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;
import "../libraries/TokenConfigs.sol";

interface IIntegrationMap {
    event TokenSettingToggled(
        address indexed token,
        TokenConfigs.TokenSettingName indexed settingName,
        bool indexed newValue
    );

    event TokenSettingUpdated(
        address indexed token,
        TokenConfigs.TokenSettingName indexed settingName,
        uint256 indexed newValue
    );

    event BiosAddressUpdated(address newAddress, address oldAddress);

    struct Integration {
        bool added;
        string name;
    }

    struct Token {
        uint256 id;
        bool added;
        bool acceptingDeposits;
        bool acceptingWithdrawals;
        bool acceptingLping;
        bool acceptingBridging;
        uint256 biosRewardWeight;
        uint256 reserveRatioNumerator;
        uint256 targetLiquidityRatioNumerator;
        uint256 transferFeeKValueNumerator;
        uint256 transferFeePlatformRatioNumerator;
    }

    /// @param contractAddress The address of the integration contract
    /// @param name The name of the protocol being integrated to
    function addIntegration(address contractAddress, string memory name) external;

    /// @param tokenAddress The address of the ERC20 token contract
    /// @param acceptingDeposits Whether token deposits are enabled
    /// @param acceptingWithdrawals Whether token withdrawals are enabled
    /// @param acceptingLping Whether LPing is enabled
    /// @param acceptingBridging Whether bridging is enabled
    /// @param biosRewardWeight Token weight for BIOS rewards
    /// @param reserveRatioNumerator Number that gets divided by reserve ratio denominator to get reserve ratio
    /// @param targetLiquidityRatioNumerator Number that gets divided by target liquidity ratio denominator to get target liquidity ratio
    /// @param transferFeeKValueNumerator Number that gets divided by transfer fee K-value denominator to get K-value
    /// @param transferFeePlatformRatioNumerator Number that gets divided by transfer fee platform ratio denominator to get the ratio of transfer fees sent to the platform instead of LPers
    function addToken(
        address tokenAddress,
        bool acceptingDeposits,
        bool acceptingWithdrawals,
        bool acceptingLping,
        bool acceptingBridging,
        uint256 biosRewardWeight,
        uint256 reserveRatioNumerator,
        uint256 targetLiquidityRatioNumerator,
        uint256 transferFeeKValueNumerator,
        uint256 transferFeePlatformRatioNumerator
    ) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function enableTokenDeposits(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function disableTokenDeposits(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function enableTokenWithdrawals(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function disableTokenWithdrawals(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function enableTokenLping(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function disableTokenLping(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function enableTokenBridging(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    function disableTokenBridging(address tokenAddress) external;

    /// @param tokenAddress The address of the token ERC20 contract
    /// @param rewardWeight The updated token BIOS reward weight
    function updateTokenRewardWeight(address tokenAddress, uint256 rewardWeight) external;

    /// @param tokenAddress the address of the token ERC20 contract
    /// @param reserveRatioNumerator Number that gets divided by reserve ratio denominator to get reserve ratio
    function updateTokenReserveRatioNumerator(address tokenAddress, uint256 reserveRatioNumerator) external;

    /// @param tokenAddress the address of the token ERC20 contract
    /// @param targetLiquidityRatioNumerator Number that gets divided by target liquidity ratio denominator to get target liquidity ratio
    function updateTokenTargetLiquidityRatioNumerator(address tokenAddress, uint256 targetLiquidityRatioNumerator)
        external;

    /// @param tokenAddress the address of the token ERC20 contract
    /// @param transferFeeKValueNumerator Number that gets divided by transfer fee K-value denominator to get K-value
    function updateTokenTransferFeeKValueNumerator(address tokenAddress, uint256 transferFeeKValueNumerator) external;

    /// @param tokenAddress the address of the token ERC20 contract
    /// @param transferFeePlatformRatioNumerator Number that gets divided by transfer fee platform ratio denominator to get the ratio of transfer fees sent to the platform instead of LPers
    function updateTokenTransferFeePlatformRatioNumerator(
        address tokenAddress,
        uint256 transferFeePlatformRatioNumerator
    ) external;

    /// @param integrationId The ID of the integration
    /// @return The address of the integration contract
    function getIntegrationAddress(uint256 integrationId) external view returns (address);

    /// @param integrationAddress The address of the integration contract
    /// @return The name of the of the protocol being integrated to
    function getIntegrationName(address integrationAddress) external view returns (string memory);

    /// @return The address of the WETH token
    function getWethTokenAddress() external view returns (address);

    /// @return The address of the BIOS token
    function getBiosTokenAddress() external view returns (address);

    /// @param tokenId The ID of the token
    /// @return The address of the token ERC20 contract
    function getTokenAddress(uint256 tokenId) external view returns (address);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The index of the token in the tokens array
    function getTokenId(address tokenAddress) external view returns (uint256);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The token BIOS reward weight
    function getTokenBiosRewardWeight(address tokenAddress) external view returns (uint256);

    /// @return rewardWeightSum reward weight of depositable tokens
    function getBiosRewardWeightSum() external view returns (uint256 rewardWeightSum);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return bool indicating whether depositing this token is currently enabled
    function getTokenAcceptingDeposits(address tokenAddress) external view returns (bool);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return bool indicating whether withdrawing this token is currently enabled
    function getTokenAcceptingWithdrawals(address tokenAddress) external view returns (bool);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return bool indicating whether Lping this token is currently enabled
    function getTokenAcceptingLping(address tokenAddress) external view returns (bool);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return bool indicating whether bridging this token is currently enabled
    function getTokenAcceptingBridging(address tokenAddress) external view returns (bool);

    // @param tokenAddress The address of the token ERC20 contract
    // @return bool indicating whether the token has been added
    function getIsTokenAdded(address tokenAddress) external view returns (bool);

    // @param integrationAddress The address of the integration contract
    // @return bool indicating whether the integration has been added
    function getIsIntegrationAdded(address tokenAddress) external view returns (bool);

    /// @notice get the length of supported tokens
    /// @return The quantity of tokens added
    function getTokenAddressesLength() external view returns (uint256);

    /// @notice get the length of supported integrations
    /// @return The quantity of integrations added
    function getIntegrationAddressesLength() external view returns (uint256);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The number that gets divided by reserve ratio denominator
    function getTokenReserveRatioNumerator(address tokenAddress) external view returns (uint256);

    /// @return The reserve ratio denominator
    function getReserveRatioDenominator() external view returns (uint32);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The number that gets divided by target liquidity ratio denominator
    function getTokenTargetLiquidityRatioNumerator(address tokenAddress) external view returns (uint256);

    /// @return The target liquidity ratio denominator
    function getTargetLiquidityRatioDenominator() external view returns (uint32);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The number that gets divided by transfer fee K-value denominator
    function getTokenTransferFeeKValueNumerator(address tokenAddress) external view returns (uint256);

    /// @return The transfer fee K-value denominator
    function getTransferFeeKValueDenominator() external view returns (uint32);

    /// @param tokenAddress The address of the token ERC20 contract
    /// @return The number that gets divided by transfer fee platform ratio denominator
    function getTokenTransferFeePlatformRatioNumerator(address tokenAddress) external view returns (uint256);

    /// @return The transfer fee platform ratio denominator
    function getTransferFeePlatformRatioDenominator() external view returns (uint32);
}
","keccak256":"0x44303b43f04ccfdbbc2cdd156b4b6b8062f27c73c286f2092411edb90b57871b","license":"GPL-2.0"},"contracts/interfaces/IKernel.sol":{"content":"// SPDX-License-Identifier: GPL-2.0
pragma solidity 0.8.4;

interface IKernel {
    struct WithdrawAllAndClaimResponse {
        uint256[] tokenAmounts;
        uint256 ethWithdrawn;
        uint256 ethClaimed;
        uint256 biosClaimed;
    }
    event Withdraw(address indexed user, address[] tokens, uint256[] tokenAmounts, uint256 ethAmount);
    event ClaimEthRewards(address indexed user, uint256[] strategies, uint256 ethRewards);
    event ClaimBiosRewards(address indexed user, uint256 biosRewards);
    event WithdrawAllAndClaim(
        address indexed user,
        address[] tokens,
        uint256[] strategies,
        bool withdrawWethAsEth,
        uint256[] tokenAmounts,
        uint256 ethWithdrawn,
        uint256 ethRewards,
        uint256 biosRewards
    );
    event TokenAdded(
        address indexed tokenAddress,
        bool acceptingDeposits,
        bool acceptingWithdrawals,
        bool acceptingLping,
        bool acceptingBridging,
        uint256 biosRewardWeight,
        uint256 reserveRatioNumerator,
        uint256 targetLiquidityRatioNumerator,
        uint256 transferFeeKValueNumerator,
        uint256 transferFeePlatformRatioNumerator
    );

    event GasAccountUpdated(address gasAccount);
    event TreasuryAccountUpdated(address treasuryAccount);
    event IntegrationAdded(address indexed contractAddress, string name);
    event SetBiosRewardsDuration(uint32 biosRewardsDuration);
    event SeedBiosRewards(uint256 biosAmount);
    event Deploy();
    event HarvestYield();
    event DistributeEth();
    event BiosBuyBack();
    event EthDistributionWeightsUpdated(
        uint32 biosBuyBackEthWeight,
        uint32 treasuryEthWeight,
        uint32 protocolFeeEthWeight,
        uint32 rewardsEthWeight,
        uint32 syntheticPoolSupplementWeight
    );
    event GasAccountTargetEthBalanceUpdated(uint256 gasAccountTargetEthBalance);

    /// @param account The address of the account to check if they are a manager
    /// @return Bool indicating whether the account is a manger
    function isManager(address account) external view returns (bool);

    /// @param account The address of the account to check if they are an owner
    /// @return Bool indicating whether the account is an owner
    function isOwner(address account) external view returns (bool);

    /// @param account The address of the account to check if they are a liquidity provider
    /// @return Bool indicating whether the account is a liquidity provider
    function isLiquidityProvider(address account) external view returns (bool);
}
