pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";

import "../interfaces/ISyntheticERC20.sol";
import "../interfaces/ISyntheticMap.sol";
import "../interfaces/IProxyCloneFactory.sol";

import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";

contract SyntheticMap is Initializable, ModuleMapConsumer, Controlled, ISyntheticMap {
    IProxyCloneFactory ProxyCloneFactory;
    ISyntheticERC20.Float ratio;

    mapping(address => address) public override syntheticToUnderlying;
    mapping(address => address) public override underlyingToSynthetic;

    function initialize(
        address[] calldata controllers_,
        address _moduleMap,
        address _ProxyCloneFactory,
        ISyntheticERC20.Float calldata _ratio,
        address wnative,
        address synWnative
    ) external initializer {
        __Controlled_init(controllers_, _moduleMap);
        ProxyCloneFactory = IProxyCloneFactory(_ProxyCloneFactory);
        ratio = _ratio;

        underlyingToSynthetic[wnative] = synWnative;
        syntheticToUnderlying[synWnative] = wnative;
    }

    function createSyntheticsForUnderlyings(address[] calldata _tokens) external onlyManager {
        for (uint256 i; i < _tokens.length; i++) _deploySyntheticTokenIfNotExist(_tokens[i]);
    }

    function deploySyntheticTokenIfNotExist(address _underlying)
        external
        override
        onlyController
        returns (address deployedSynthetic_)
    {
        deployedSynthetic_ = _deploySyntheticTokenIfNotExist(_underlying);
    }

    function _deploySyntheticTokenIfNotExist(address _underlying) internal returns (address deployedSynthetic_) {
        if (underlyingToSynthetic[_underlying] == address(0)) {
            deployedSynthetic_ = _deploySyntheticToken(_underlying);

            underlyingToSynthetic[_underlying] = deployedSynthetic_;
            syntheticToUnderlying[deployedSynthetic_] = _underlying;
        }
    }

    function _deploySyntheticToken(address _underlying) private returns (address) {
        IERC20MetadataUpgradeable uToken = IERC20MetadataUpgradeable(_underlying);
        string memory symbol = string(abi.encodePacked("syn", uToken.symbol()));
        string memory name = string(abi.encodePacked("Synthetic ", uToken.name()));

        return
            ProxyCloneFactory.deployClone(
                0xb5600855841ea3a7f3ccea1415cb04f021f04bdeea47fe1b5d5e7326707b04bb, // keccak256("SyntheticERC20")
                abi.encode(
                    name,
                    symbol,
                    to2ComponentArray(
                        moduleMap.getModuleAddress(Modules.YieldManager),
                        moduleMap.getModuleAddress(Modules.UserPositions)
                    ),
                    address(moduleMap),
                    ratio
                )
            );
    }

    function to2ComponentArray(address e0, address e1) private pure returns (address[] memory) {
        address[] memory array = new address[](2);

        array[0] = e0;
        array[1] = e1;

        return array;
    }
}
