pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../interfaces/ISyntheticERC20.sol";
import "../interfaces/IUserPositions.sol";
import "../interfaces/IGenericInitializable.sol";

import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";

/**
 * @title ERC20 + additional debt accounting for hedge pool
 * @notice Synthetic pool used to get a credit for user withdrawal
 * @dev This contract allows YieldManager and UserPositions to sustain synthetic and undelying flow presented below:
 * Conventions: x - hedge percent, y = 1 - x, z = 1 + x, n/m ≡ n [underlying]/ m [synthetic]
 * n/m ∈ account123 means that account123 has n undelying and m synthetic at it's balance
 *
 * ● User calls enterStrategy: x/x goes to hedge pool, y/0 goes to actual strategy, 0/y goes to kernel, -1/0 goes to user
 *     BALANCES: x/x ∈ hedge pool, y/0 ∈ actual strategy, 0/y ∈ kernel, -1/0 ∈ user
 * ...
 * ● User calls withdraw, kernel has no drifting money: swap synthetic for underlying via hedge pool:
 * -1/1 goes to hedge pool, 0/0 goes to actual strategy, 0/-1 goes to kernel, 1/0 goes to user
 *     BALANCES: -y/z ∈ hedge pool, y/0 ∈ actual strategy, 0/-x ∈ kernel, 0/0 ∈ user
 * ● Admin calls YieldManager::deploy(), part 1: withdraw y/0 from the actual strategy and swap underlying
 * for synthetic via hedge pool: y/-y goes to hedge pool, -y/0 goes to actual strategy, 0/y goes to kernel
 *     BALANCES: 0/2 ∈ hedge pool*x, 0/0 ∈ actual strategy, 0/y-x ∈ kernel
 * ● Admin calls YieldManager::deploy(), part 2: withdraw 0/2*x from the hedge pool, 0/2*x goes to kernel
 *     BALANCES: 0/0 ∈ hedge pool, 0/1 ∈ kernel
 **/
contract SyntheticERC20 is
    Initializable,
    ModuleMapConsumer,
    Controlled,
    ERC20Upgradeable,
    ISyntheticERC20,
    IGenericInitializable
{
    // Percent of enter strategy value that will go to the synthetic pool
    Float hedgePercent;

    // Synthetic pool hedgeIntegration and pool id
    address private hedgeIntegration;
    uint32 private ammPoolID;

    // Limit that will be not exeeded via SwapManager::gentleSwapExactIn
    PoolRatioLimit poolRatioLimit;

    // When we execute UserPositions._findMoney -> SwapManager.gentleSwapExactIn for x synETH:
    // 1) If received x ETH: do not change debt
    // 2) Else if we received x + y ETH: decrease debt by y
    // 3) Else if we received x - y ETH: increase debt by y
    int256 underlyingDebt;

    // When we use synthetic pool for withdraw, we earn a debt,
    // that will be paid on the next YieldManager::deploy() call
    StrategiesDebt strategiesDebt;

    // When user enter to strategy and cover some negative deploy amount, we should pay strategies debt and swap synthetic back
    // but for beter user experience we delay it until YieldManager::deploy() call
    uint256 delayedRedemptionOfSynthetic;

    // When burning if kernel don't have enough money it will get burnDebt, which will be repaid in the next mint call
    // This debt has nothing to do with the debts earned by swaping via a hedge pool
    uint256 kernelBurnDebt;

    function initialize(bytes calldata _data) external override initializer {
        (
            string memory name,
            string memory symbol,
            address[] memory controllers_,
            address moduleMap_,
            Float memory _hedgePercent
        ) = abi.decode(_data, (string, string, address[], address, Float));

        __ERC20_init(name, symbol);
        __Controlled_init(controllers_, moduleMap_);
        hedgePercent = _hedgePercent;
    }

    /**
     *
     *
     * Admin functions
     *
     *
     **/

    function setupCrvPool(
        address _integration,
        uint32 _ammPoolID,
        PoolRatioLimit calldata _poolRatioLimit
    ) external onlyManager {
        hedgeIntegration = _integration;
        ammPoolID = _ammPoolID;
        poolRatioLimit.underlying = _poolRatioLimit.underlying;
        poolRatioLimit.synthetic = _poolRatioLimit.synthetic;
    }

    function setIntegration(address _integration) external onlyManager {
        hedgeIntegration = _integration;
    }

    function setAMMPoolID(uint32 _newAMMPoolID) external onlyManager {
        ammPoolID = _newAMMPoolID;
    }

    /**
     * @dev
     * Ratio must take decimals into account, so if desired pool ratio is x:y
     * and tokens decimals is x:18 and y:6 then poolRatioLimit should be x/1e18:y/1e6
     **/
    function setPoolRatioLimit(PoolRatioLimit calldata _poolRatioLimit) external onlyManager {
        poolRatioLimit.underlying = _poolRatioLimit.underlying;
        poolRatioLimit.synthetic = _poolRatioLimit.synthetic;
    }

    function setHedgePercent(Float calldata _value) external onlyManager {
        hedgePercent = _value;
    }

    /**
     *
     *
     * Default ERC20
     *
     *
     **/

    // We assume that mint and burn functions will be called only for kernel account
    function mint(address _account, uint256 _amount) external override onlyController {
        uint256 burnDebt = kernelBurnDebt;
        if (burnDebt > 0) {
            if (_amount > burnDebt) {
                _mint(_account, _amount - burnDebt);
                kernelBurnDebt = 0;
            } else {
                kernelBurnDebt -= _amount;
            }
        } else {
            _mint(_account, _amount);
        }
    }

    // We assume that mint and burn functions will be called only for kernel account
    function burn(address _account, uint256 _amount) external override onlyController {
        uint256 balance = balanceOf(_account);
        if (_amount > balance) {
            kernelBurnDebt += _amount - balance;
            _amount = balance;
        }
        _burn(_account, _amount);
    }

    function balanceOf(address account) public view override(ISyntheticERC20, ERC20Upgradeable) returns (uint256) {
        return ERC20Upgradeable.balanceOf(account);
    }

    function totalSupply() public view override(ERC20Upgradeable, ISyntheticERC20) returns (uint256) {
        return ERC20Upgradeable.totalSupply();
    }

    /**
     *
     *
     * Synthetic pool debt management
     *
     *
     **/

    /**
     * @dev
     * After using hedge pool for deploy we will earn 2 debts:
     * 1) Underlying: y = swap(synthetic, underlying, x), y != x
     * 2) StrategiesDebt:
     *     a) SD.swapBackSynthetic: underlying amount holded by actual strategy,
     *         we have to withdraw that amount from integrations and swap it back for synthetic
     *     b) SD.withdrawSynthetic: synthetic amount deposited earlier to the synthetic pool as "hedge percent",
     *         we have to withdraw it from the pool
     **/
    function changePoolDebt(uint256 syntheticSpended, uint256 underlyingReceived) external override onlyController {
        if (syntheticSpended > underlyingReceived) {
            underlyingDebt += int256(syntheticSpended - underlyingReceived);
        } else if (syntheticSpended < underlyingReceived) {
            underlyingDebt -= int256(underlyingReceived - syntheticSpended);
        }
        uint256 poolDebt = (underlyingReceived * hedgePercent.numerator) / hedgePercent.denominator;
        uint256 integrationDebt = underlyingReceived - poolDebt;

        strategiesDebt.swapBackSynthetic += uint128(integrationDebt);
        strategiesDebt.withdrawSynthetic += uint128(poolDebt);
    }

    /**
     * @dev
     * When we use hedge pool when user withdraws his funds, we earn strategies debt and negative deployAmount
     * This debt can be paid two ways:
     * 1) YieldManager::deploy(): move debt from actual strategy to hedge pool
     * 2) Another user enters any strategy and his deposit can be used to pay this debt
     *
     * Problem: in the 2 option we will increase strategy and therefore will rid of negative deployAmount,
     * without paying debt (due to absence of negative deployAmount we can't relay on 1 option),
     * so user have to pay this debt and swap undelying for synthetic, but in order to make enterStrategy function
     * cheaper we will delay this swap until next deploy function
     **/
    function increaseRedemptionOfSynthetic(uint256 _amount) external override onlyController {
        uint256 debt = uint256(strategiesDebt.swapBackSynthetic);
        if (debt < _amount) _amount = debt;
        delayedRedemptionOfSynthetic += _amount;
        strategiesDebt.swapBackSynthetic -= uint128(_amount);
    }

    /// @dev After perfoming delayed swap in YieldManager::deploy() function (check this.increaseRedemptionOfSynthetic description)
    function getAndDeleteDelayedRedemptionOfSynthetic() external override onlyController returns (uint256 _amount) {
        _amount = delayedRedemptionOfSynthetic;
        delete delayedRedemptionOfSynthetic;
    }

    /**
     * @dev
     * When we execute UserPositions._findMoney -> SwapManager.gentleSwapExactIn for x synETH:
     * 1) If received x ETH: do not change debt
     * 2) Else if we received x + y ETH: debt -= y
     * 3) Else if we received x - y ETH: debt += y
     *
     * In order to pay this debt before deploy to hedgeIntegration we should adjust deposit amount
     * For example we want to provide z liquidity to hedge pool and we have y underlying debt (z > y):
     * 1) If y == 0:
     *          underlyingDeposit = z
     *          syntheticDeposit = z
     * 2) Else if y > 0:
     *          underlyingDeposit = z - y
     *          syntheticDeposit = z
     * 3) Else if y < 0:
     *          underlyingDeposit = z
     *          syntheticDeposit = z - y
     **/
    function adjustLiquidityAccordingToUnderlyingDebt(uint256 _desiredLiquidity)
        external
        override
        onlyController
        returns (uint256 _underlyingLiquidity, uint256 _syntheticLiquidity)
    {
        _underlyingLiquidity = _desiredLiquidity;
        _syntheticLiquidity = _desiredLiquidity;

        if (underlyingDebt > 0) {
            _underlyingLiquidity = safeSubtract(_desiredLiquidity, abs(underlyingDebt));
            underlyingDebt -= int256(_desiredLiquidity - _underlyingLiquidity);
        } else if (underlyingDebt < 0) {
            _syntheticLiquidity = safeSubtract(_desiredLiquidity, abs(underlyingDebt));
            underlyingDebt += int256(_desiredLiquidity - _syntheticLiquidity);
        }
    }

    /// @dev When we perform negative deploy we have to pay our debts to synthetic pool if have any
    function distributeAccordingToStrategiesDebt(uint256 _amount)
        external
        override
        onlyController
        returns (uint256 hold_, uint256 underlyingInToSwap_)
    {
        hold_ = _amount;

        if (strategiesDebt.swapBackSynthetic > 0) {
            hold_ = safeSubtract(_amount, strategiesDebt.swapBackSynthetic);
            underlyingInToSwap_ = _amount - hold_;

            strategiesDebt.swapBackSynthetic -= uint128(underlyingInToSwap_);
        }
    }

    /**
     * @dev
     * Conventions: x - hedge percent, y = 1 - x, z = 1 + x, n/m ≡ (n [underlying]/ m [synthetic])
     * n/m ∈ account123 means that account123 has n undelying and m synthetic at it's balance
     *
     * ● User calls enterStrategy: x/x goes to hedge pool, y/0 goes to actual strategy, 0/y goes to kernel, -1/0 goes to user
     *     BALANCES: x/x ∈ hedge pool, y/0 ∈ actual strategy, 0/y ∈ kernel, -1/0 ∈ user
     * ...
     * ● User calls withdraw, kernel has no drifting money: swap synthetic for underlying via hedge pool:
     * -1/1 goes to hedge pool, 0/0 goes to actual strategy, 0/-1 goes to kernel, 1/0 goes to user
     *     BALANCES: -y/z ∈ hedge pool, y/0 ∈ actual strategy, 0/-x ∈ kernel, 0/0 ∈ user
     * ● Admin calls YieldManager::deploy(), part 1: withdraw y/0 from the actual strategy and swap underlying
     * for synthetic via hedge pool: y/-y goes to hedge pool, -y/0 goes to actual strategy, 0/y goes to kernel
     *     BALANCES: 0/2 ∈ hedge pool*x, 0/0 ∈ actual strategy, 0/y-x ∈ kernel
     * ● Admin calls YieldManager::deploy(), part 2: withdraw 0/2*x from the hedge pool, 0/2*x goes to kernel
     *     BALANCES: 0/0 ∈ hedge pool, 0/1 ∈ kernel
     *
     * 0/1 kernel will hold until next enterStrategy in order to don't do unnecessary burn(kernel, x), mint(kernel, x)
     *
     * We increase strategiesDebt.withdrawSynthetic by x, but at the last step pool will has 0/2*x,
     * that is why we multiplystrategiesDebt.withdrawSynthetic by 2 below
     **/
    function getAndDeleteStrategiesPoolDebt() external override onlyController returns (uint256 debt_) {
        debt_ = strategiesDebt.withdrawSynthetic * 2;
        delete strategiesDebt.withdrawSynthetic;
    }

    function safeSubtract(uint256 _minuend, uint256 _subtrahend) private pure returns (uint256 _result) {
        if (_subtrahend > _minuend) {
            _result = 0;
        } else {
            _result = _minuend - _subtrahend;
        }
    }

    function abs(int256 val) private pure returns (uint256) {
        return uint256(val >= 0 ? val : -val);
    }

    /**
     *
     *
     * Getters
     *
     *
     **/

    /// @dev Get info about hedgeIntegration for deploy (check YieldManager.deploy)
    function getHedgeDeployArgs()
        external
        view
        override
        returns (
            address integration_,
            uint32 ammPoolID_,
            address syntheticToken_
        )
    {
        integration_ = hedgeIntegration;
        ammPoolID_ = ammPoolID;
        syntheticToken_ = address(this);
    }

    function getHedgePercent() external view override returns (uint128 numerator_, uint128 denominator_) {
        numerator_ = hedgePercent.numerator;
        denominator_ = hedgePercent.denominator;
    }

    /**
     * @dev
     * Get info about limit ratio for synthetic pool (check UserPositions._findMoney)
     * Ratio must take decimals into account, so if desired pool ratio is x:y
     * and tokens decimals is x:18 and y:6 then poolRatioLimit should be x*1e18:y*1e6
     **/
    function getPoolLimit() external view override returns (uint32 underlying_, uint32 synthetic_) {
        underlying_ = poolRatioLimit.underlying;
        synthetic_ = poolRatioLimit.synthetic;
    }

    function getDebts() external view returns (int256, uint256) {
        return (underlyingDebt, kernelBurnDebt);
    }

    /**
     *
     *
     * Migration
     *
     *
     **/

    function migrate(address _recipient, uint256 _amount) external onlyManager {
        _mint(_recipient, _amount);
    }
}
