pragma solidity 0.8.4;
import "./IIntegration.sol";
import "./IUserPositions.sol";

interface IStrategyMap {
    /**
     *
     * structs
     *
     **/

    struct DeployAmount {
        int128 deploy;
        uint128 hedge;
    }

    struct Integration {
        address integration;
        uint32 ammPoolID;
    }
    struct Token {
        uint256 integrationPairIdx;
        address token;
        uint32 weight;
    }

    struct Strategy {
        string name;
        Integration[] integrations;
        Token[] tokens;
        mapping(address => bool) availableTokens;
        mapping(address => uint256) maximumCap;
    }

    struct StrategySummary {
        string name;
        Integration[] integrations;
        Token[] tokens;
    }

    struct TokenFunds {
        uint256 totalBalance;
        // strategy token => strategy balance
        mapping(address => uint256) strategyBalance;
    }

    struct ClosablePosition {
        address integration;
        uint32 ammPoolID;
        uint256 amount;
    }

    struct ReplaceIntegrationParams {
        Integration newIntegration;
        address token;
        uint256 oldIntegrationIndex;
        uint32[] weightAmounts;
        uint256[] weightTokenIndexes;
    }

    /**
     *
     *
     * events
     *
     *
     **/

    event NewStrategy(uint256 indexed strategyId, Integration[] integrations, Token[] tokens, string name);

    event UpdateName(uint256 indexed strategyId, string name);

    event UpdateStrategy(uint256 indexed strategyId, Integration[] integrations, Token[] tokens);

    event DeleteStrategy(uint256 indexed strategyId);

    event IntegrationWeightAdjustment(
        uint256 strategyId,
        uint256 sourceIndex,
        uint256 destinationIndex,
        uint32 amount,
        bool rebalance
    );

    event RemoveIntegration(uint256 strategyId, uint256 integrationIndex, address token);

    event AddIntegration(uint256 strategyId, Integration integration, address token);

    /**
     *
     *
     * manager's functions
     *
     *
     **/

    /**
     @notice Adds a new strategy to the list of available strategies
     @param name  the name of the new strategy
     @param integrations  the integrations and weights that form the strategy
     @param maxCap  The maximum amount investable in a strategy
     */
    function addStrategy(
        string calldata name,
        Integration[] calldata integrations,
        Token[] calldata tokens,
        uint256[] memory maxCap
    ) external;

    /**
    @notice Updates the strategy name
    @param name  the new name
     */
    function updateName(uint256 strategyId, string calldata name) external;

    function addPairToStrategy(
        uint256 strategyId,
        Integration calldata integration,
        address token
    ) external;

    /**
    @dev Changes weights of integrations,optionally rebalancing the integrations
        so that current fund allocation reflects new weights
     */
    function movePairWeight(
        uint256 strategyId,
        uint256 sourceIndex,
        uint256 destinationIndex,
        int256 vectorAmount,
        uint32 amount,
        bool rebalance
    ) external;

    function removePairFromStrategy(
        uint256 strategyId,
        uint256 integrationIndex,
        address token
    ) external;

    /**
    @notice Deletes a strategy
    @dev This can only be called successfully if the strategy being deleted doesn't have any assets invested in it.
    @dev To delete a strategy with funds deployed in it,
        first update the strategy so that the existing tokens are no longer available in the strategy,
        then delete the strategy. This will unwind the users positions,
        and they will be able to withdraw their funds.
    @param strategyId  the strategy to delete
     */
    function deleteStrategy(uint256 strategyId) external;

    /**
    @notice Sets availability status for the token
    @param strategyId The strategy strategyId
    @param token The token address
    @param available An availability status. Velue "true" will enable token and vice versa
    */
    function setAvailableTokens(
        uint256 strategyId,
        address token,
        bool available
    ) external;

    /**
    @notice Sets the maximum investment cap for a strategy and token pair
    @param strategyId The strategy strategyId
    @param token the token address
    @param maxCap The maximum amount of funds that can be invested
    */
    function setMaximumCap(
        uint256 strategyId,
        address token,
        uint256 maxCap
    ) external;

    /**
     *
     *
     * strategy flow
     *
     *
     **/

    /**
    @notice Increases the amount of a set of tokens in a strategy
    @param strategyId  the strategy to deposit into
    @param token Token address
    @param amount Amount of the token
    @return strategyDebtClosed_ How much negative deploy amount was increased (-x [deploy] + y [amount] = y - x [deploy])
     */
    function increaseStrategy(
        uint256 strategyId,
        address token,
        uint256 amount
    ) external returns (uint256 strategyDebtClosed_);

    /**
    @notice Decreases the amount of a set of tokens invested in a strategy
    @param strategyId  the strategy to withdraw assets from
    @param amount  amount of lp token that user have burned
     */
    function decreaseStrategy(uint256 strategyId, uint256 amount) external;

    /**
     *
     *
     * deploy flow
     *
     *
     **/

    /**
    @notice Decreases the deployable amount after a deployment/withdrawal
    @param integration  the integration that was changed
    @param poolID  the pool within the integration that handled the tokens
    @param token  the token to decrease for
    @param deployAmount Amount that went to the corresponding pool
    @param hedgeAmount Amount that went to the synthetic pool
     */
    function decreaseDeployAmountChange(
        address integration,
        uint32 poolID,
        address token,
        uint256 deployAmount,
        uint256 hedgeAmount
    ) external;

    /**
     *
     *
     * getters
     *
     *
     **/

    /**
    @notice Getter function to return the nested arrays as well as the name
    @param strategyId  the strategy to return
     */
    function getStrategy(uint256 strategyId) external view returns (StrategySummary memory);

    /**
    @notice Returns 1 or more strategies in a single call.
    @param strategyIds to return.
    */
    function getMultipleStrategies(uint256[] calldata strategyIds) external view returns (StrategySummary[] memory);

    /**
    @notice Returns the amount of a given token currently invested in a strategy
    @param strategyId  the strategy strategyId to check
    @param token  The token to retrieve the balance for
    @return amount  the amount of token that is invested in the strategy
     */
    function getStrategyTokenBalance(uint256 strategyId, address token) external view returns (uint256 amount);

    /**
    @notice Returns the total amount of a token invested across all strategies
    @param token  the token to fetch the balance for
    @param strategies An array of strategies in which total balance will be calculated
    @return amount  the amount of the token currently invested
    */
    function getTokenTotalBalance(address token, uint256[] calldata strategies) external view returns (uint256 amount);

    /**
    @notice Returns the current amount awaiting deployment
    @param integration  the integration to deploy to
    @param poolID  the pool within the integration that should receive the tokens
    @param token  the token to be deployed
    @return deployAmount An amount that should be deployed to the corresponding integration
    @return hedgeAmount An amount that should be deployed to the synthetic pool 
     */
    function getDeployAmount(
        address integration,
        uint32 poolID,
        address token
    ) external view returns (int256 deployAmount, uint256 hedgeAmount);

    /// @notice autogenerated getter definition
    function idCounter() external view returns (uint256);

    /**
    @notice returns the length of the tokens array in a strategy
    @param strategy  the strategy to look up
    @return the length
    */
    function getStrategyTokenLength(uint256 strategy) external view returns (uint256);

    /// @notice Retrieves the maximum cap for a strategy/token pair
    /// @param strategyId  the strategy strategyId
    /// @param token  the token address
    /// @return cap  the maximum cap for the strategy/token pair
    function getMaximumCap(uint256 strategyId, address token) external view returns (uint256 cap);
}
