pragma solidity 0.8.4;

interface ISyntheticERC20 {
    struct PoolRatioLimit {
        uint32 underlying;
        uint32 synthetic;
    }

    struct Float {
        uint128 numerator;
        uint128 denominator;
    }

    struct StrategiesDebt {
        uint128 swapBackSynthetic;
        uint128 withdrawSynthetic;
    }

    /**
     *
     *
     * Default ERC20
     *
     *
     **/

    function balanceOf(address _account) external view returns (uint256);

    function totalSupply() external view returns (uint256);

    function mint(address _account, uint256 _amount) external;

    function burn(address _account, uint256 _amount) external;

    /**
     *
     *
     * Synthetic pool debt management
     *
     *
     **/

    /**
     * @param syntheticSpended How much synthetic was spended to the swap
     * @param underlyingReceived How much underlying was received from the swap
     **/
    function changePoolDebt(uint256 syntheticSpended, uint256 underlyingReceived) external;

    function increaseRedemptionOfSynthetic(uint256 _amount) external;

    function getAndDeleteDelayedRedemptionOfSynthetic() external returns (uint256 _amount);

    /**
     * @dev
     * When we execute UserPositions._findMoney -> SwapManager.gentleSwapExactIn for x synETH:
     * 1) If received x ETH: do not change debt
     * 2) Else if we received x + y ETH: debt -= y
     * 3) Else if we received x - y ETH: debt += y
     *
     * In order to pay this debt before deploy to hedge integration we should adjust deposit amount
     * For example we want to provide z liquidity to hedge pool and we have y underlying debt (z > y):
     * 1) If y == 0:
     *          underlyingDeposit = z
     *          syntheticDeposit = z
     * 2) Else if y > 0:
     *          underlyingDeposit = z - y
     *          syntheticDeposit = z
     * 3) Else if y < 0:
     *          underlyingDeposit = z
     *          syntheticDeposit = z - y
     **/
    function adjustLiquidityAccordingToUnderlyingDebt(uint256 _desiredLiquidity)
        external
        returns (uint256 _underlyingLiquidity, uint256 _syntheticLiquidity);

    /**
     * @dev
     * When we use synthetic pool for withdraw, we eanr a debt that will be paid on the next deploy call.
     * This method will calculate how much underlying have to be deposited to synthetic pool and reduce debt
     **/
    function distributeAccordingToStrategiesDebt(uint256 _gotFromWithdrawal)
        external
        returns (uint256 hold_, uint256 depositToPool_);

    function getAndDeleteStrategiesPoolDebt() external returns (uint256);

    /**
     *
     *
     * Getters
     *
     *
     **/

    /**
     * @return integration_ Integration in which hedge funds will go
     * @return ammPoolID_ Integration AMM pool ID
     * @return syntheticToken_ Address of the SyntheticERC20 contract on the chain
     **/
    function getHedgeDeployArgs()
        external
        view
        returns (
            address integration_,
            uint32 ammPoolID_,
            address syntheticToken_
        );

    function getHedgePercent() external view returns (uint128 numerator_, uint128 denominator_);

    /**
     * @dev Kernel will be able to withdraw underlying from crv pool
     * until pool reach (underlying_ : synthetic_) ratio
     **/
    function getPoolLimit() external view returns (uint32 underlying_, uint32 synthetic_);
}
