pragma solidity >=0.7.6;

/// @title TokenConfigs contains enum and events for the different token settings used in events in Kernel and IntegrationMap
library TokenConfigs {
    enum TokenSettingName {
        rewardWeight,
        reserveRatioNumerator,
        targetLiquidityRatioNumerator,
        transferFeeKValueNumerator,
        transferFeePlatformRatioNumerator,
        deposit,
        withdraw,
        lp,
        bridge
    }

    event TokenSettingUpdated(address indexed token, TokenSettingName indexed settingName, uint256 indexed newValue);

    event TokenSettingToggled(address indexed token, TokenSettingName indexed settingName, bool indexed newValue);
}
