        external
        view
        returns (uint256);

    /// @param asset Address of the ERC20 token contract
    function getTokenLpUsers(address asset)
        external
        view
        returns (address[] memory);

    /// @param asset Address of the ERC20 token contract
    function getTokenProtocolFeeRewards(address asset)
        external
        view
        returns (uint256);
}
