pragma solidity 0.8.4;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "../core/Controlled.sol";
import "../core/ModuleMapConsumer.sol";
import "../interfaces/IAMMIntegration.sol";
import "../interfaces/IDragonLair.sol";
import "../interfaces/IIntegrationMap.sol";
import "../interfaces/ISushiSwapFactory.sol";
import "../interfaces/ISushiSwapRouter.sol";
import "../interfaces/ISushiSwapPair.sol";
import "../interfaces/IStakingRewards.sol";
import "../interfaces/IERC20.sol";
import "../interfaces/IWeth9.sol";
import "../libraries/AMMLibrary.sol";

/// @notice Integrates 0x Nodes to QuickSwap in Polygon
/// Stakes against https://github.com/QuickSwap/quickswap-core/blob/master/contracts/staking/StakingRewards.sol
/// The StakingRewards only send back rewards when explicitly harvested
contract PolygonQuickSwapIntegration is Controlled, IAMMIntegration {
    using SafeERC20Upgradeable for IERC20MetadataUpgradeable;

    address private constant DQUICK_ADDRESS = 0xf28164A485B0B2C90639E47b0f377b4a438a16B1;
    address private constant QUICK_ADDRESS = 0x831753DD7087CaC61aB5644b308642cc1c33Dc13;
    uint24 private constant SLIPPAGE_DENOMINATOR = 1_000_000;
    uint24 slippageNumerator;

    address factoryAddress;
    address swapRouterAddress;
    address wnativeAddress;

    mapping(uint256 => Pool) private pools;
    uint32 public poolCount;
    uint32[] private poolIds;
    // PoolId => StakingRewards address
    mapping(uint256 => IStakingRewards) private stakings;
    // PoolId => Token => Amount
    mapping(uint256 => mapping(address => uint256)) private balances;

    // yield amounts
    mapping(uint32 => uint256) public yieldBalances;

    // total harvested yield (weth) per pool (harvested by YieldManager)
    mapping(uint32 => uint256) private harvestedYield;

    // reward allocated per pool
    mapping(uint32 => uint256) private rewardBalances;

    // the reward token of a pool
    mapping(uint32 => address) private rewardToken;

    event TokensReceived(uint256 amount);
    event LPTokensAcquired(uint256 amount);
    event DepositToPool(uint256 poolId, uint256 amount);
    event WithdrawnFromPool(uint256 poolId, uint256 amount);
    event YieldReceived(uint256 amount);
    event PoolRebalanced(
        uint256 poolId,
        address tokenSold,
        uint256 amountSold,
        address tokenBought,
        uint256 amountBought
    );

    receive() external payable {}

    /// @param controllers_ The address of the controlling contract
    /// @param factoryAddress_ The address of the QuickSwap factory contract
    /// @param swapRouterAddress_ The address of the Quick swap router contract
    function initialize(
        address[] memory controllers_,
        address moduleMap_,
        address factoryAddress_,
        address swapRouterAddress_,
        uint24 slippageNumerator_
    ) public initializer {
        require(factoryAddress_ != address(0), "bad address");
        require(swapRouterAddress_ != address(0), "bad address");

        __Controlled_init(controllers_, moduleMap_);
        factoryAddress = factoryAddress_;
        swapRouterAddress = swapRouterAddress_;
        slippageNumerator = slippageNumerator_;
        wnativeAddress = IIntegrationMap(moduleMap.getModuleAddress(Modules.IntegrationMap)).getWethTokenAddress();
    }

    function createPool(
        address tokenA,
        address tokenB,
        uint256 // unused
    ) external onlyManager {
        poolCount++;
        pools[poolCount].tokenA = tokenA;
        pools[poolCount].tokenB = tokenB;
        poolIds.push(poolCount);

        if (IERC20MetadataUpgradeable(tokenA).allowance(address(this), swapRouterAddress) != type(uint256).max) {
            IERC20MetadataUpgradeable(tokenA).safeApprove(swapRouterAddress, 0);
            IERC20MetadataUpgradeable(tokenA).safeApprove(swapRouterAddress, type(uint256).max);
        }

        if (IERC20MetadataUpgradeable(tokenB).allowance(address(this), swapRouterAddress) != type(uint256).max) {
            IERC20MetadataUpgradeable(tokenB).safeApprove(swapRouterAddress, 0);
            IERC20MetadataUpgradeable(tokenB).safeApprove(swapRouterAddress, type(uint256).max);
        }
    }

    function configureStaking(uint32 poolId, address stakingRewards) external onlyManager {
        _configureStaking(poolId, stakingRewards, false);
    }

    function configureStakingOverwrite(uint32 poolId, address stakingRewards) external onlyManager {
        _configureStaking(poolId, stakingRewards, true);
    }

    function _configureStaking(
        uint32 poolId,
        address stakingRewards_,
        bool overwrite
    ) internal {
        Pool memory pool = getPool(poolId);
        require(pool.tokenA != address(0), "Pool doesn't exist");

        if (overwrite != true) {
            require(address(stakings[poolId]) == address(0), "Staking already configured");
        }

        address pairAddress = pairFor(pool.tokenA, pool.tokenB);

        IStakingRewards stakingRewards = IStakingRewards(stakingRewards_);
        require(stakingRewards.stakingToken() == pairAddress, "Incorrect staking address");

        stakings[poolId] = stakingRewards;
        rewardToken[poolId] = stakingRewards.rewardsToken();

        // let staking contract pull tokens
        if (IERC20MetadataUpgradeable(pairAddress).allowance(address(this), stakingRewards_) != type(uint256).max) {
            IERC20MetadataUpgradeable(pairAddress).safeApprove(stakingRewards_, 0);
            IERC20MetadataUpgradeable(pairAddress).safeApprove(stakingRewards_, type(uint256).max);
        }
    }

    function updateRewardToken(uint32[] memory pids) external onlyManager {
        for (uint256 i; i < pids.length; ++i) {
            address token = stakings[pids[i]].rewardsToken();
            require(token != address(0), "Invalid rewards token");

            rewardToken[pids[i]] = token;
        }
    }

    /// @param tokenAddress The address of the deposited token
    /// @param amount The amount of the token being deposited
    function deposit(
        address tokenAddress,
        uint256 amount,
        uint32 poolId
    ) external override onlyController {
        balances[poolId][tokenAddress] += amount;
    }

    // @param token The address of the deposited token
    /// @param amount The amount of the token being deposited
    function incrementBalance(
        uint32 poolId,
        address token,
        uint256 amount
    ) external onlyManager {
        balances[poolId][token] += amount;
    }

    // @param token The address of the deposited token
    /// @param amount The amount of the token being decreased
    function decrementBalance(
        uint32 poolId,
        address token,
        uint256 amount
    ) external onlyManager {
        balances[poolId][token] -= amount;
    }

    function getPool(uint32 pid) public view returns (Pool memory) {
        return pools[pid];
    }

    /// @return tokenAddress The address of the token to get the balance of
    function getBalance(uint32 poolId, address tokenAddress) public view returns (uint256) {
        return balances[poolId][tokenAddress];
    }

    function getPoolBalance(uint32 poolId) external view returns (uint256 tokenA, uint256 tokenB) {
        (tokenA, tokenB) = getTokensPoolValue(poolId);
    }

    function deploy(uint32 poolId) external override onlyController {
        _deploy(poolId);
    }

    function manualDeploy(uint32 poolId) external onlyManager {
        _deploy(poolId);
    }

    function _deploy(uint32 poolId) internal {
        Pool memory pool = getPool(poolId);

        require(pools[poolId].tokenA != address(0), "Pool doesn't exist");

        uint256 balanceA = getBalance(poolId, pool.tokenA);
        uint256 balanceB = getBalance(poolId, pool.tokenB);

        if (balanceA == 0 || balanceB == 0) {
            return;
        } else {
            (uint256 reserveA, uint256 reserveB) = getReserves(pool.tokenA, pool.tokenB);

            (uint256 amountA, uint256 amountB) = AMMLibrary.getDeployAmounts(balanceA, balanceB, reserveA, reserveB);

            (, , uint256 liquidityAcquired) = ISushiSwapRouter(swapRouterAddress).addLiquidity(
                pool.tokenA,
                pool.tokenB,
                amountA,
                amountB,
                0,
                0,
                address(this),
                block.timestamp
            );

            balances[poolId][pool.tokenA] -= amountA;
            balances[poolId][pool.tokenB] -= amountB;

            emit LPTokensAcquired(liquidityAcquired);
            emit DepositToPool(poolId, liquidityAcquired);
        }
    }

    function stakeLPTokens(uint32 poolId) external onlyManager {
        Pool memory pool = getPool(poolId);

        uint256 balance = IERC20MetadataUpgradeable(pairFor(pool.tokenA, pool.tokenB)).balanceOf(address(this));
        stakings[poolId].stake(balance);
    }

    function harvestYield() external override onlyController {
        uint256 yieldAmount;
        for (uint256 i; i < poolCount; ++i) {
            uint32 poolId = poolIds[i];
            uint256 poolYield = yieldBalances[poolId];
            if (poolYield > 0) {
                yieldAmount += poolYield;
                harvestedYield[poolId] += poolYield;

                // yield balance will be shipped to YieldManager
                yieldBalances[poolId] = 0;
            }
        }
        if (yieldAmount > 0) {
            IERC20MetadataUpgradeable(wnativeAddress).safeTransfer(
                moduleMap.getModuleAddress(Modules.YieldManager),
                yieldAmount
            );
            emit YieldReceived(yieldAmount);
        }
    }

    /// @notice Harvest available yield for desired pool
    // TODO make this work :)
    function harvestYieldByPool(
        uint32 poolId,
        uint256 rewardRatioX1000, // price ratio for rewards token! if dquick, use quick instead
        uint256, // unused ratio
        bool // unused
    ) external onlyManager {
        address token = rewardToken[poolId];

        // check initial balances
        uint256 amountBefore = _selfBalance(token);

        // harvest
        stakings[poolId].getReward();

        // check and convert
        uint256 rewardAmount = _selfBalance(token) - amountBefore;

        if (rewardAmount > 0) {
            address actualToken;
            uint256 actualAmount;
            if (token == DQUICK_ADDRESS) {
                // We got DQUICK, so unstake it and swap the received QUICK
                actualToken = QUICK_ADDRESS;
                actualAmount = _unstake(rewardAmount);
            } else {
                // we got a regular token, so hopefully we can just liquidate it
                actualToken = token;
                actualAmount = rewardAmount;
            }

            checkPriceCeiling(actualToken, wnativeAddress, rewardRatioX1000);
            uint256[] memory amounts = _swapExactInput(
                actualToken,
                wnativeAddress,
                address(this),
                actualAmount,
                getAmountOutMinimum(actualToken, wnativeAddress, actualAmount) / 20
            );
            yieldBalances[poolId] += amounts[1];
        }
    }

    function _unstake(uint256 dQuickAmount) internal returns (uint256 quickAmount) {
        if (dQuickAmount > 0) {
            uint256 amountBefore = _selfBalance(QUICK_ADDRESS);

            // unstake the DQuick
            IDragonLair(DQUICK_ADDRESS).leave(dQuickAmount);

            quickAmount = _selfBalance(QUICK_ADDRESS) - amountBefore;
        }
    }

    /// @notice Withdraws token from the integration
    /// @param tokenAddress The address of the underlying token to withdraw
    /// @param amount The amoutn of the token to withdraw
    function withdraw(
        address tokenAddress,
        uint256 amount,
        uint32 poolId
    ) public override onlyController {
        require(amount <= getBalance(poolId, tokenAddress), "integration: balance exceeded");

        balances[poolId][tokenAddress] -= amount;

        IERC20MetadataUpgradeable(tokenAddress).safeTransfer(moduleMap.getModuleAddress(Modules.Kernel), amount);
    }

    /// @notice Withdraws token from the integration
    /// @param tokenAddress The address of the underlying token to withdraw
    /// @param amount The amoutn of the token to withdraw
    function withdraw(
        address tokenAddress,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000
    ) public onlyController {
        if (amount <= getBalance(poolId, tokenAddress)) {
            IERC20MetadataUpgradeable(tokenAddress).safeTransfer(moduleMap.getModuleAddress(Modules.Kernel), amount);
            balances[poolId][tokenAddress] -= amount;
        } else {
            _withdraw(tokenAddress, amount, poolId, ratioX1000, true);
        }
    }

    function manualWithdraw(
        address tokenAddress,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000
    ) external onlyManager {
        _withdraw(tokenAddress, amount, poolId, ratioX1000, false);
    }

    function getTokensPoolValue(uint32 poolId)
        internal
        view
        returns (uint256 amountOfTokenAInPool, uint256 amountOfTokenBInPool)
    {
        Pool memory pool = getPool(poolId);

        uint256 lpAmount = IERC20(pairFor(pool.tokenA, pool.tokenB)).balanceOf(address(this)) +
            stakings[poolId].balanceOf(address(this));

        (amountOfTokenAInPool, amountOfTokenBInPool) = AMMLibrary.getAmountOfTokensInPool(
            lpAmount,
            IERC20(pairFor(pool.tokenA, pool.tokenB)).totalSupply(),
            IERC20(pool.tokenA).balanceOf(pairFor(pool.tokenA, pool.tokenB)),
            IERC20(pool.tokenB).balanceOf(pairFor(pool.tokenA, pool.tokenB))
        );
    }

    function getLiquidityToWithdraw(
        uint32 poolId,
        address tokenAddress,
        uint256 amount
    ) internal view returns (uint256 liquidityToWithdraw) {
        Pool memory pool = getPool(poolId);

        (uint256 reserve0, uint256 reserve1) = getReserves(pool.tokenA, pool.tokenB);

        uint256 totalSupply = IERC20(pairFor(pool.tokenA, pool.tokenB)).totalSupply();

        uint256 availableLiquidity = stakings[poolId].balanceOf(address(this));

        uint256 tokensPerLP;

        if (tokenAddress == pool.tokenA) {
            tokensPerLP = totalSupply == 0 ? 0 : (reserve0 * 1e18) / totalSupply;
        } else {
            tokensPerLP = totalSupply == 0 ? 0 : (reserve1 * 1e18) / totalSupply;
        }

        if (tokensPerLP == 0) {
            return 0;
        }

        // half amount because we will liquidate the other half
        liquidityToWithdraw = ((amount / 2) * 1e18) / tokensPerLP;

        if (liquidityToWithdraw > availableLiquidity) {
            liquidityToWithdraw = availableLiquidity;
        }
    }

    function _withdraw(
        address tokenAddress,
        uint256 amount,
        uint32 poolId,
        uint256 ratioX1000,
        bool sendToKernel
    ) internal {
        Pool memory pool = getPool(poolId);

        if (ratioX1000 > 0) {
            checkPriceCeiling(pool.tokenA, pool.tokenB, ratioX1000);
        }

        uint256 liquidityToWithdraw = getLiquidityToWithdraw(poolId, tokenAddress, amount);

        stakings[poolId].withdraw(liquidityToWithdraw);

        IERC20MetadataUpgradeable(pairFor(pool.tokenA, pool.tokenB)).safeApprove(
            swapRouterAddress,
            liquidityToWithdraw
        );

        (uint256 amountTokenA, uint256 amountTokenB) = ISushiSwapRouter(swapRouterAddress).removeLiquidity(
            pool.tokenA,
            pool.tokenB,
            liquidityToWithdraw,
            0,
            0,
            address(this),
            block.timestamp + 360
        );

        withdrawSwapAndEmit(pool, poolId, tokenAddress, amountTokenA, amountTokenB, sendToKernel);
    }

    function withdrawSwapAndEmit(
        Pool memory pool,
        uint32 poolId,
        address tokenAddress,
        uint256 amountTokenA,
        uint256 amountTokenB,
        bool sendToKernel
    ) internal {
        uint256[] memory amountsOfTokenReceived;

        if (tokenAddress == pool.tokenA) {
            amountsOfTokenReceived = _swapExactInput(
                pool.tokenB,
                tokenAddress,
                address(this),
                amountTokenB,
                getAmountOutMinimum(pool.tokenB, tokenAddress, amountTokenB)
            );

            if (sendToKernel) {
                IERC20MetadataUpgradeable(pool.tokenA).safeTransfer(
                    moduleMap.getModuleAddress(Modules.Kernel),
                    amountTokenA + amountsOfTokenReceived[1]
                );
            } else {
                balances[poolId][pool.tokenA] += (amountTokenA + amountsOfTokenReceived[1]);
            }

            emit WithdrawnFromPool(poolId, amountsOfTokenReceived[1] + amountTokenA);
        } else if (tokenAddress == pool.tokenB) {
            amountsOfTokenReceived = _swapExactInput(
                pool.tokenA,
                tokenAddress,
                address(this),
                amountTokenA,
                getAmountOutMinimum(pool.tokenA, tokenAddress, amountTokenA)
            );

            if (sendToKernel) {
                IERC20MetadataUpgradeable(pool.tokenB).safeTransfer(
                    moduleMap.getModuleAddress(Modules.Kernel),
                    amountTokenB + amountsOfTokenReceived[1]
                );
            } else {
                balances[poolId][pool.tokenB] += (amountTokenB + amountsOfTokenReceived[1]);
            }

            emit WithdrawnFromPool(poolId, amountsOfTokenReceived[1] + amountTokenB);
        }
    }

    /// @param tokenIn The address of the input token
    /// @param tokenOut The address of the output token
    /// @param recipient The address of the token out recipient
    /// @param amountIn The exact amount of the input to swap
    function _swapExactInput(
        address tokenIn,
        address tokenOut,
        address recipient,
        uint256 amountIn,
        uint256 amountOutMin
    ) internal returns (uint256[] memory) {
        address[] memory path = new address[](2);
        path[0] = tokenIn;
        path[1] = tokenOut;
        uint256 deadline = block.timestamp;

        if (IERC20MetadataUpgradeable(tokenIn).allowance(address(this), swapRouterAddress) < amountIn) {
            IERC20MetadataUpgradeable(tokenIn).safeApprove(swapRouterAddress, 0);
            IERC20MetadataUpgradeable(tokenIn).safeApprove(swapRouterAddress, amountIn);
        }

        return
            ISushiSwapRouter(swapRouterAddress).swapExactTokensForTokens(
                amountIn,
                amountOutMin,
                path,
                recipient,
                deadline
            );
    }

    // external for swap manager
    function swapExactInput(
        address tokenIn,
        address tokenOut,
        address recipient,
        uint256 amountIn,
        uint256 amountOutMin
    ) external onlyController returns (uint256[] memory) {
        return _swapExactInput(tokenIn, tokenOut, recipient, amountIn, amountOutMin);
    }

    /// @param tokenIn The address of the input token
    /// @param tokenOut The address of the output token
    /// @param amountIn The exact amount of the input to swap
    /// @return amountOutMinimum The minimum amount of tokenOut to receive, factoring in allowable slippage
    function getAmountOutMinimum(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) public view returns (uint256 amountOutMinimum) {
        amountOutMinimum =
            (getAmountOut(tokenIn, tokenOut, amountIn) * (SLIPPAGE_DENOMINATOR - slippageNumerator)) /
            SLIPPAGE_DENOMINATOR;
    }

    /// @param tokenIn The address of the input token
    /// @param tokenOut The address of the output token
    /// @param amountIn The exact amount of the input to swap
    /// @return amountOut The estimated amount of tokenOut to receive
    function getAmountOut(
        address tokenIn,
        address tokenOut,
        uint256 amountIn
    ) public view returns (uint256 amountOut) {
        (uint256 reserveIn, uint256 reserveOut) = getReserves(tokenIn, tokenOut);
        amountOut = AMMLibrary.getAmountOut(amountIn, reserveIn, reserveOut);
    }

    /// @param tokenA The address of tokenA
    /// @param tokenB The address of tokenB
    /// @return reserveA The reserve balance of tokenA in the pool
    /// @return reserveB The reserve balance of tokenB in the pool

    function getReserves(address tokenA, address tokenB) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = AMMLibrary.sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = ISushiSwapPair(pairFor(tokenA, tokenB)).getReserves();
        (reserveA, reserveB) = tokenA == token0 ? (reserve0, reserve1) : (reserve1, reserve0);
    }

    /// @param tokenA The address of tokenA
    /// @param tokenB The address of tokenB

    /// @return pair The address of the Sushi pool contract
    function pairFor(address tokenA, address tokenB) internal view returns (address pair) {
        pair = ISushiSwapFactory(factoryAddress).getPair(tokenA, tokenB);
    }

    /// @dev Returns total amount of pending yield in SUSHI rewards
    /// @param poolId The integration pool to check for pending SUSHI rewards
    /// @return tokens Token addresses of of the pending yield
    /// @return amounts Amounts of pending yield
    function getPendingYield(uint32 poolId)
        external
        view
        returns (address[2] memory tokens, uint256[2] memory amounts)
    {
        return (
            [stakings[poolId].rewardsToken(), wnativeAddress],
            [stakings[poolId].earned(address(this)), yieldBalances[poolId]]
        );
    }

    function getStakingAddress(uint32 poolId) external view returns (address) {
        return address(stakings[poolId]);
    }

    function getYieldBalances(uint32[] calldata pids) external view returns (uint256[] memory amounts) {
        amounts = new uint256[](pids.length);
        for (uint256 i; i < pids.length; ++i) {
            amounts[i] = _getYieldBalance(pids[i]);
        }
    }

    function _getYieldBalance(uint32 poolId) internal view returns (uint256) {
        return yieldBalances[poolId];
    }

    function getHarvestedYield(uint32 poolId) public view returns (uint256) {
        return harvestedYield[poolId];
    }

    function getTotalHarvestedYield() public view returns (uint256 amount) {
        for (uint32 i; i < poolCount; ++i) {
            amount += getHarvestedYield(i + 1);
        }
    }

    /// @notice Rebalances by attempting to swap up to max to get into position ratio
    /// @param poolId The ID of the pool to rebalance
    /// @param ratioX1000 reserves ratio to control the price slippage
    /// @param maxSellTokenA max amount of tokenA to sell (to limit rebalancing)
    /// @param maxSellTokenB max amount of tokenB to sell (to limit rebalancing)
    function rebalancePool(
        uint32 poolId,
        uint256 ratioX1000,
        uint256 maxSellTokenA,
        uint256 maxSellTokenB
    ) external onlyManager {
        Pool memory pool = getPool(poolId);

        require(pools[poolId].tokenA != address(0), "Pool doesn't exist");

        checkPriceCeiling(pool.tokenA, pool.tokenB, ratioX1000);

        // determine which token and how much to swap
        (address swapToken, uint256 excessAmountToSwap) = calculateExcessTokensToSwap(
            pool,
            getBalance(poolId, pool.tokenA),
            getBalance(poolId, pool.tokenB)
        );

        // handle tokenA vs tokenB, cap at max sell param

        (uint256 swapAmount, address targetToken) = AMMLibrary.getRebalanceAmounts(
            swapToken,
            pool,
            excessAmountToSwap,
            maxSellTokenA,
            maxSellTokenB
        );

        // track balance change of targetToken
        uint256 targetBalanceBefore = IERC20MetadataUpgradeable(targetToken).balanceOf(address(this));

        // swap tokens
        _swapExactInput(
            swapToken,
            targetToken,
            address(this),
            swapAmount,
            getAmountOutMinimum(swapToken, targetToken, swapAmount)
        );

        uint256 amountReceived = IERC20MetadataUpgradeable(targetToken).balanceOf(address(this)) - targetBalanceBefore;

        // update pool token balances
        if (amountReceived > 0) {
            balances[poolId][swapToken] -= swapAmount;
            balances[poolId][targetToken] += amountReceived;

            emit PoolRebalanced(poolId, swapToken, swapAmount, targetToken, amountReceived);
        }
    }

    function calculateExcessTokensToSwap(
        Pool memory pool,
        uint256 balanceA,
        uint256 balanceB
    ) internal view returns (address swapToken, uint256 excessAmountToSwap) {
        require(balanceA + balanceB > 0, "QuickSwapIntegration: no balance");
        // fetch reserve values from quickswap
        (uint256 reserveA, uint256 reserveB) = getReserves(pool.tokenA, pool.tokenB);

        (swapToken, excessAmountToSwap) = AMMLibrary.getExcessTokenAmounts(
            pool,
            balanceA,
            balanceB,
            reserveA,
            reserveB
        );
    }

    function checkPriceCeiling(
        address tokenA,
        address tokenB,
        uint256 ratioX1000
    ) internal view {
        address pairAddress = address(pairFor(tokenA, tokenB));
        (uint256 reserveA, uint256 reserveB) = getReserves(tokenA, tokenB);

        AMMLibrary.checkPriceCeiling(pairAddress, reserveA, reserveB, ratioX1000);
    }

    function getRewardBalances(uint32[] calldata pids) external view returns (uint256[] memory rewardAmounts) {
        rewardAmounts = new uint256[](pids.length);
        for (uint256 i; i < pids.length; ++i) {
            rewardAmounts[i] = rewardBalances[pids[i]];
        }
    }

    function _selfBalance(address token) internal view returns (uint256) {
        return IERC20MetadataUpgradeable(token).balanceOf(address(this));
    }
}
